css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://todoist.com") {
body{margin:0 0 0 0 !important;font-family: 'Helvetica Std Black' !important;background: url("http://kttns.org/k2yt") !important;overflow-x:hidden !important;}
 #content{position:absolute !important;margin-top: -17px;padding:0; display:block !important;width:100% !important;}
.text_cursor {padding-left: 4px !important;background: #3d3d3d !important;} 
#top{position:absolute;width:100%;display:block;margin-left:166px; text-align:left !important;overflow:inherit !important;}
 #logo{display:none;}
 
/** HELP */
 #info_container {width:65% !important;background: #eee !important;-moz-border-radius: 25px;left: 205px !important;top:50px !important;margin-bottom: 15px !important;border-top: 1px solid white;-moz-box-shadow: 0 5px 25px black;}
 #info_container li{color:grey !important;}
 #info_container h2{text-shadow: 0 1px 0 #fff !important;}
 
/** HELP END */
 #editor {position:absolute;width:65% !important;margin: 50px -50px 0 165px !important;display: block !important;}
 #top_right a{display:none;}
 #top_right{position:fixed !important;z-index:450 !important;display:block; margin-top:-13px !important;margin-bottom:-13px !important;padding-left:40px !important;background:url("http://kttns.org/rhow") 0 -143px repeat-x !important;width:100%;height:29px !important;text-align:left !important;text-shadow: 0 1px 0 #d4d4d4 !important;}
 #indicator{display:none;}
 #bottom{display:none;}
 #agenda{border-bottom: 1px solid #a8aeb6 !important;width: 123px !important;-moz-box-shadow: 0px 1px 0px #e8ecf1 !important;padding: 0 0 0 22px !important;background: url("http://kttns.org/rhow") no-repeat 1px -176px !important;}
 .input_q{display:block;position:fixed !important;right: 0px !important;top: 20px !important;z-index:600;background: #909090 url("http://kttns.org/rhow") no-repeat 5px -128px;border-top: 1px solid #5d5d5d;border-left: 0px solid white;border-right: 0px solid white;border-bottom: 0px solid #dadada;-moz-border-radius: 8px;-moz-box-shadow: 0 1px 0 #f0f0f0;margin-top: -12px !important;padding-left:16px;width: 130px !important;z-index:450; font-size: 12px !important;}
 .search_img{display:none}
 form .item_table{ color:#d6dde5 !important; }
 .print_icon{position:fixed;margin: -33px 0 0px -38px;z-index:451 !important;clip:rect(2px 60px 20px 30px) !important;}
 .div_counter{float:right;position:absolute;display:block;text-align:center;margin-left:120px;-moz-border-radius: 4px; font-size: 10px !important;color: #333 !important; margin-top: 1px !important;}
 .indent_2 .div_counter{margin-left:110px !important;}
 .indent_3 .div_counter{margin-left:105px !important;}
 .indent_4 .div_counter{margin-left:100px !important;}
 .name{text-shadow: 0 1px 0 #f5f9ff !important;}
 .current .name{text-shadow: 0 1px 0 black !important;}
 .items li {background-color: transparent !important;padding:0 0 1px 0 !important;font-size: 16px !important;color:#4c5866;border-top: 1px solid #d6dde5 !important;border-bottom: 1px solid #d6dde5 !important;margin-bottom: 0px !important;margin-bottom: 0 !important; }
 #project_man{position:absolute !important;display:block;z-index:250;bottom:0px !important;background: url("http://kttns.org/rhow") 0 -105px repeat-x;padding:3px 0 6px 10px !important;margin:0 0 0 -15px !important;width:155px;}
 #project_editor li{background: #3d3d3d url("http://kttns.org/zmnm") repeat-x !important;margin-bottom:5px !important;border: 1px solid #6a6a6a !important;display:block;-moz-border-radius: 8px;padding: 3px 3px 3px 7px !important;color: #c0c0c0 !important;text-shadow: 0 1px 0px #000;z-index:600 !important;}
 #project_history li{background: grey !important;margin-bottom:5px !important;border: 1px solid #6a6a6a !important;display:block;-moz-border-radius: 8px;padding: 3px 3px 3px 3px !important;color: #000 !important;text-shadow: 0 1px 0px #a4a4a4;}
 #agenda_view li{background: #3d3d3d url("http://kttns.org/zmnm") repeat-x !important;margin-bottom:5px !important;border: 1px solid #6a6a6a !important;display:block;-moz-border-radius: 8px;padding: 3px 0px 3px 3px !important;color: #c0c0c0 !important;text-shadow: 0 1px 0px #000;z-index:600 !important;}
 .clickable{text-shadow: 0 0 0 !important;}
 .date{text-shadow: 0 0 0 !important;}
 .date empty{background: transparent !important;}
 .selected { border-left: 3px solid #000 !important; background-color: transparent !important; }
 .selected .content { background-color: transparent !important; }
 .selected_top { padding-top: 20px !important; }
 #project_list .indent_2 { margin-left: 10px !important;}
 #project_list .indent_3 { margin-left: 15px !important; }
 #project_list .indent_4 { margin-left: 30px !important; }
 .items .indent_1 { }
  .items .indent_2 { margin-left: 10px !important; }
 .items .indent_3 { margin-left: 20px !important;}
 .items .indent_4 { margin-left: 40px !important; }
 .items .indent_5 { margin-left: 60px !important; }
 .items .indent_6 { margin-left: 60px !important; }
 .items .indent_7, .indent_8, .indent_9 {  margin-left: 60px !important; }
 #agenda_view{width:100% !important;padding: 0px !important; margin: 0 !important;left:50px !important;}
 .a_counter{display: none !important;}
 
/** Typo */
 #today_link{display:block;width:65px;float:left;}
 #today_count{display:block;float:right;text-align:center;width: 16px !important;-moz-border-radius: 5px;background:red !important;margin:2px 85px 0 0 !important;}
 .help{display:none;}
 form .action{float:left;clear:left;text-transform:uppercase;font-size:14px !important;font-weight:bold;color: #4c5866 !important;text-shadow: 0px 1px 0px #f5f6f8;padding-left:5px !important;height:17px !important; }
 form .action a:hover{float:left; clear:left; text-transform:uppercase; font-size:14px !important;font-weight:bold;color: #4c5866 !important;text-shadow: 0px 1px 0px #f5f6f8;padding-left:5px !important; }
 .mini_menu{display:none;}
 h2{font-size: 16px !important;color: black !important; text-shadow: 0px 1px 0px #848484;font-weight:bold !important; text-transform:uppercase; text-align: center !important;margin: -10px 0 2px -10% !important;}
 .date {margin:1px 0 0 0 !important;padding:1px 2px 0px 2px !important;-moz-border-radius:4px;font-size: 15px !important;}
 .clickable {margin:1px 0 0 0 !important;padding:1px 2px 1px 2px !important;-moz-border-radius: 3px;font-size: 15px !important;}
 .h2_date{color:#333 !important;font-weight: normal !important;}
 .history_desc{clear:left;display:block !important;border:0 !important;font-size: 16px !important;font-weight:bold; color: black !important; text-shadow: 0 1px 0 #848484;margin-left:5px !important;border-bottom:1px solid black !important;-moz-box-shadow: 0 1px 0 #848484; }
 .reorder_on{ text-decoration: none; background-color: grey !important; padding: 2px 3px 2px 3px; -moz-border-radius:3px; }
 
/** Link */
 .actions a { margin-right: 10px; cursor: default; }
 a.action { padding: 1px; text-decoration: none; color: #000; cursor: default; }
 a.action:hover { background-color:transparent !important; }
 a.name{cursor:default !important;}
 .clickable { cursor: default !important; }
 .controller a{color:#000 !important;text-shadow: 0 1px 0 #848484;}
 .controller {color:#000 !important;padding: 3px 0px 10px 5px !important;font-size: 15px !important;margin: 0px 40px 25px 0;float:left;display:block;width:85%;clear:left !important;}
 #project_man{margin-left: -10px !important; height:24px;overflow: hidden !important;display: block !important;}
 #project_man a{display:block !important;float:left;height: 35px !important;background: transparent !important;border-right: 1px solid #cecece;-moz-box-shadow: 1px 0px 0px #f8f8f8 !important;padding:0px 6px 0 0px !important; font-size:14px !important;color: #6d6d6d !important;text-shadow: 0 1px 0 #f8f8f8 !important;overflow: hidden !important;width:64px !important;line-height:20px !important;margin:-1px 5px 0 0 !important;}
 #project_list .current { background-color: #5f93d4 !important; color: white !important; font-weight:bold !important; text-shadow: 0 0px 0 white !important; margin-left: -10px !important; padding-left: 10px !important; -moz-border-radius: 0px !important; border-top: 1px solid #477fc6 !important; border-bottom: 1px solid #1f55ab !important; }
 .indent_2 .current {margin:5px !important;padding-left:0px !important;}
 .priority_1 { color: #000; }
 .priority_2 { color: #008800; }
 .priority_3 { color: #0079B5; }
 .priority_4 { color: #ff0000; }
 .priority_1 a { color: white !important; }
 .priority_2 a { color: yellow !important; }
 .priority_3 a { color: orange !important; }
 .priority_4 a { color: red !important; }
 
/** Other */
 .checkbox { width:20px; height:15px; margin: 2px 0 -3px 3px !important;   overflow: hidden !important; background: url("http://kttns.org/rhow") no-repeat !important; }
 .checkbox:hover {  background: url("http://kttns.org/rhow") no-repeat 0 -15px !important; }
 .checkbox:active {  background: url("http://kttns.org/rhow") no-repeat 0 -30px !important; }
 .div_checker .checkbox { width:20px; height:15px; margin: 1px 0 -3px 3px !important;   overflow: hidden !important; background: url("http://kttns.org/rhow") no-repeat 0 -30px !important;}
 .div_checker .checkbox:hover { background: url("http://kttns.org/rhow") no-repeat 0 0px !important;}
 .checked .date, .checked .time, .checked .project span { background-color: transparent !important; color: #333 !important; }
 .checked .text { background-color: transparent !important; text-decoration: line-through; }
 #list .text_box {width: 87% !important; border: 0px !important;}
 #project_editor .arrow {position: absolute !important;margin:-1px 0 0 -18px !important;cursor: pointer;}
 #project_list .arrow {position: absolute !important;margin:-1px 0 0 -10px !important;cursor: pointer;}
 .AmiMenu { cursor: default; position: absolute; top: 0; left: 0; background-color: black !important ; opacity: 0.50; -moz-border-radius: 8px !important; padding:2px !important; border:1px solid black !important; z-index: 505; color: white !important; font-weight: bold !important; }
 .AmiMenu .no_hover { padding-right: 3px; color: red !important; background-color: #000 !important; display: block; width: 110px !important; font-weight: bold !important; }
 .AmiMenu .on { background: #5170f6; color: #fff; text-shadow: 0 1px 0 black; }
 .auto_exapnd_ta, .auto_exapnd_clone { font-size: 16px !important; color: #000 !important; letter-spacing: 0.8px; border: 0px !important; background: white !important; overflow-x: hidden; opacity: 0.75 !important; }
 .due_date{ font-size: 16px !important; color: #000 !important; letter-spacing: 0.8px; border: 0px !important; background: white !important; overflow-x: hidden; opacity: 0.75 !important;}
 .desc{color: white !important;}
 #editor .error { color: black !important;font-weight: bold !important;text-shadow: 0 1px 0 #848484;font-size: 16px !important;}
 #editor .light_error { color: red !important;font-weight: bold !important;text-shadow: 0 1px 0 #848484;font-size: 16px !important;}
 #item_commander{width:100% !important;}
 
/** Images */
 .cmp_menu_off { background: url("http://kttns.org/rhow") 0 -45px no-repeat !important; width: 15px; font-size: 1px; height: 14px !important; }
 .cmp_menu_on { background: url("http://kttns.org/rhow") 0 -45px no-repeat !important; width: 15px; font-size: 1px; height: 15px !important; }
 .view_holder { border: 0 !important; position:fixed !important; top:6px; left:185px; z-index:800;}
 .cmp_gear_on { background: url("http://kttns.org/rhow") 0 -75px no-repeat !important; width: 16px !important; font-size: 1px; height: 15px !important; margin-top:2px;}
 .cmp_gear_off { background: url("http://kttns.org/rhow") 0 -90px no-repeat !important; width: 16px !important; font-size: 1px; height: 15px !important; margin-top:2px;}
 
/** WINDOW */
 #GB_overlay { background-color: transparent !important; position: absolute; margin: auto; top: 0px !important; left: 0; }
 #GB_window { left: 0; top: 45px !important; font-size: 1px; position: absolute; overflow: visible; z-index: 4000 !important; -moz-box-shadow: 0 0 20px black; }
 #GB_window .content { width: auto; margin: 0 0 0 0 !important; padding: 5px !important; background: white !important; }
 .GB_Gallery { margin: 0 22px 0 22px; }
 #GB_middle img { display:none !important; cursor: pointer; vertical-align: middle; }
 #GB_middle .disabled { cursor: default; }
 #GB_middle .left { padding-right: 10px; }
 #GB_middle .right { padding-left: 10px; }
 .GB_Window .content { border: 0px solid #ccc !important; color: black !important; border-top: none; margin:0 10px 0 0 !important; padding:0 !important; height:400px !important; }
 .GB_Window .header { border-top: 0px !important; border-left: 0 !important; border-right: 0 !important; border-bottom: 1px solid #aaa !important; width: 100% !important; margin-right: 0px !important; height: 22px; font-size: 16px; padding: 0 5px 0 5px !important; color: #000 !important; text-shadow: 0 1px 0 #eee; background: #eee !important; }
 .GB_Window .caption { font-size: 16px; text-align: left; font-weight: bold; white-space: nowrap; padding-right: 0px !important; }
 .GB_Window .close { text-align: right; }
 .GB_Window .close span {  font-size: 16px; cursor: pointer;  }
 .GB_Window .close img { cursor: pointer; padding: 0 3px 0 0; display:none !important; }
 .GB_Window .on { border-bottom: 0px solid #333 !important; }
 .GB_Window .click { border-bottom: 0px solid red !important; }
 .gb_iframe_html { font-size: 25px !important; color: white !important; padding: 0px !important; text-align: center !important; width: auto !important; border: 0 !important; }
 #notifier { clear:left !important; position: fixed !important; padding: 0px !important; width: 97% !important; height: 20px !important; bottom:0 !important; visibility: hidden !important; margin: 0px !important; padding:16px !important; text-align: center !important; font-size: 24px !important; font-family: "Marker Felt" !important; border-bottom: 1px !important; -moz-border-radius-bottomright:16px; -moz-border-radius-bottomleft:16px; -moz-box-shadow: 0 0 30px black; background: #ffffcc; z-index:2000 !important;  }
 .AmiTT_main { text-align: left; position: absolute; background-color: #000 !important; border: 0px solid #000 !important; -moz-border-radius: 5px; margin: 0px; padding: 2px 4px 2px 4px !important; opacity: 0.85; }
 .AmiTT_head_text { font-size: 11px; color: #fff !important; text-shadow: 0 1px 0 black; }
 .AmiTT_extra_text { font-size: 10px; color: #333; text-shadow: 0 1px 0 black; }
 .cmp_ind_r_on { background: url("http://kttns.org/rhow") 0px -285px no-repeat !important; width: 16px; font-size: 1px; height: 12px; }
 .cmp_ind_r_off { background: url("http://kttns.org/rhow") 0px -255px no-repeat !important; width: 12px; font-size: 1px; height: 15px; }
 .cmp_ind_l_on { background: url("http://kttns.org/rhow") 0px -300px no-repeat !important; width: 12px; font-size: 1px; height: 15px; }
 .cmp_ind_l_off { background: url("http://kttns.org/rhow") 0px -270px no-repeat !important; width: 12px; font-size: 1px; height: 13px; }
 
/** JS or CSS */
  #list{position:fixed !important;background:#d6dde5 !important; width:155px !important;padding: 4px 0 0 10px !important;margin-top: 0px;height:100%;;display:block;position:absolute;border-right: 1px solid #a8aeb6 !important;}
 }
