css:
@-moz-document domain("youtube.com")
{

/* Panel */
ytd-multi-page-menu-renderer[darker-dark-theme], yt-chip-cloud-chip-renderer[modern-chips][chip-style], ytd-menu-popup-renderer.style-scope.ytd-popup-container, .ytp-popup, .ytp-settings-menu
{
    background: #121212;
    box-shadow: 0px 0px 10px black !important;
    color: white;
    border-radius: 2em;
    border: none;
    outline: 1px solid #ffffff21 !important;
}

/* Background */
ytd-app[darker-dark-theme], #container.ytd-masthead, ytd-app[darker-dark-theme] #guide-content.ytd-app, ytd-rich-item-renderer, tp-yt-paper-dialog, ytd-mini-guide-renderer[darker-dark-theme], ytd-mini-guide-entry-renderer[darker-dark-theme]
{
  background-color: black;
  border: none;
}

/* Transparent */
ytd-feed-filter-chip-bar-renderer[darker-dark-theme][is-dark-theme] #right-arrow.ytd-feed-filter-chip-bar-renderer:before, ytd-feed-filter-chip-bar-renderer[darker-dark-theme][is-dark-theme] #left-arrow.ytd-feed-filter-chip-bar-renderer:after, ytd-masthead[darker-dark-theme] #background.ytd-masthead {
    background: transparent;
}

/* Text */
yt-chip-cloud-chip-renderer[chip-style=STYLE_DEFAULT], yt-chip-cloud-chip-renderer[chip-style=STYLE_HOME_FILTER], yt-chip-cloud-chip-renderer[chip-style=STYLE_REFRESH_TO_NOVEL_CHIP], #video-title.ytd-rich-grid-media, yt-icon.ytd-logo, yt-icon.guide-icon.style-scope.ytd-guide-entry-renderer, .title.ytd-guide-entry-renderer, yt-formatted-string.ytd-guide-signin-promo-renderer, #guide-section-title.ytd-guide-section-renderer, #title.ytd-rich-shelf-renderer
{
    color: white;
}

/* Text 2 */
a.yt-simple-endpoint.style-scope.yt-formatted-string, span.inline-metadata-item.style-scope.ytd-video-meta-block
{
    color: #aaaaaa !important;
}

/* Hidden */
#root.ytd-video-display-full-buttoned-renderer {
    visibility: hidden;
}
div#right-arrow-button {
    background: #0e0e0e !important;
    border-radius: 3em 0px 0px 3em;
}
div#left-arrow-button {
    background: #0e0e0e !important;
    border-radius: 0px 3em 3em 0px;
}
div#chips-wrapper {
background: linear-gradient( black, #00000082) !important;
    box-shadow: 0px 0px 68px 4px black;
}
.ytp-gradient-bottom {
    background-image: linear-gradient(transparent, transparent, black) !important;
}
#guide-inner-content.ytd-app {
    box-shadow: 20px 0px 20px black;
}
div#description {
    background: transparent !important;
}
ytd-watch-metadata[modern-metapanel] #description-inner.ytd-watch-metadata {

    margin: 0px;
}
.html5-video-player .video-click-tracking, .html5-video-player .video-stream {
    border-radius: 2em;
}
button.ytp-ad-skip-button.ytp-button {
    background: black !important;
    opacity: 100% !important;
    box-shadow: 0px 0px 10px black;
    border-radius: 2em 0px 0px 2em;
    border: none;
}
.ytp-flyout-cta .ytp-flyout-cta-body
{
    border-radius: 3em;
}
ytd-promoted-sparkles-web-renderer, #action-companion-click-target.ytd-action-companion-ad-renderer
{
    visibility: hidden;
    height: 0px;
}
ytd-button-renderer.style-scope.ytd-watch-metadata {
    box-shadow: 0px 0px 30px #ffffffaa;
    border-radius: 2em;
}
div#ryd-bar {
    box-shadow: 0px 0px 10px white;
}
h1.ytd-watch-metadata
{
filter: drop-shadow(0px 0px 20px white);
}
#panels.ytd-watch-flexy {
    height: 0px;
    visibility: hidden;
}
.ytp-ad-preview-container, .ytp-ad-message-container
{
    border-radius: 2em;
}
.badge-style-type-live-now-alternate.ytd-badge-supported-renderer {
    box-shadow: 0px 0px 30px #d01818a3;
}
#cinematics.ytd-watch-flexy {
    filter: contrast(1.2) brightness(1.3) saturate(0.75);
}
.sbdd_b {
    box-shadow: 0 2px 4px white;
    -webkit-box-shadow: 0 5px 30px white;
}
.sbdd_a {
    filter: invert(1);
}
.ytp-cued-thumbnail-overlay-image {
    border-radius: 1EM;
}
ytd-thumbnail[size=medium] a.ytd-thumbnail, ytd-thumbnail[size=medium]:before {
    border-radius: 1EM;
}
@keyframes BlursIn {
  from {        filter: saturate(0) opacity(0);}
  to {          filter: saturate(1) opacity(1);}
  }
.ghost-card.ytd-ghost-grid-renderer {
    opacity: 0.3;
}

#dismissible.ytd-rich-grid-media {
  animation-name: BlursIn;
  animation-duration: 0.8s;
}
}
