css:
@-moz-document url-prefix("https://awesome-movies.life/") {
  /* CSS code for the website goes here */
/* Set a neutral background color */
body {
    background-color: #f5f5f5;
}

/* Use a readable font and increase font size */
body, #header, h1, #message, #qq, #qfield, #movieList, .rel_infos, .rel_movie, .rel_movie a, .rel_year, .rel_genre, #footer, #options, .optselect {
    font-family: "Helvetica Neue", Arial, sans-serif;
    font-size: 16px;
}

/* Center the header and h1 elements */
#header, h1 {
    text-align: center;
}

/* Use a more legible color for text */
body, #header, h1, #message, #qfield, .rel_year {
    color: #333;
}

/* Use a more subtle background color for input field */
#qfield {
    background-color: #f2f2f2;
    border: none;
    border-bottom: solid 2px #ccc;
    padding: 10px;
    width: 60%;
    margin-bottom: 10px;
    box-sizing: border-box;
    height: 40px;
    font-size: 22px;

    background-repeat: no-repeat;
    background-position: left center;
    outline: none;
}

/* Use a muted color for the search button */
#qq {
    text-align: center;
    color: #999;
}

/* Use a more subtle background color for the movie list */
#movieList {
    display: grid;
    grid-gap: 5px;
    background-color: #f5f5f5;
    padding: 10px;
}

/* Set a max-width for the movie list */
@media screen and (min-width: 300px) {
    #movieList {
        max-width: 600px;
        margin: 0 auto;
    }
}

/* Use a more neutral color for the movie titles */
.rel_movie a {
    color: #333;
}

/* Use a more legible color for the year and genre */
.rel_year, .rel_genre {
    color: #666;
}

/* Center the footer */
#footer {
    text-align: center;
    margin: 20px 0;
}

}
