css:
@-moz-document url-prefix("https://soap2day"), url-prefix("https://soapgate.org"), url-prefix("https://s2dfree") {
    .panel-default > .panel-heading {
        color: #000!important;
        background-color: #6a6a6a!important;
        border-color: #232323!important;
    }

    body {
        background: #343434!important;
        color: #fff!important;
    }

    .panel {
        background: 0 0!important;
        border-width: thin;
        border-color: #000!important;
    }

    .myp1 {
        background-color: #4a4a4a!important;
    }

    .myp1 > a > b, .col-sm-12 > a > b {
        color: #9eeae7!important;
    }

    .panel-info {
        border-width: thin;
        border-color: #000!important;
    }

    .panel-info > .panel-heading {
        color: #fff!important;
        background-color: #2f5371!important;
        border-color: #000!important;
    }

    .form-control {
        background-color: #343434!important;
        color: #fff!important;
        border: none!important;
    }

    .thumbnail {
        background-color: #343434!important;
    }

    .thumbnail > a {
        color: #fff!important;
    }

    .alert-info-ex {
        color: #fff!important;
        background-color: #2f5371!important;
        border-color: #000!important;
    }

    .btn-info {
        background-color: #6a6a6a!important;
    }

    .label.label-info {
        background-color: #2f5371!important;
        color: #fff!important;
    }

    .col-sm-12 {
        background: none!important;
    }

    .alert-info-ex {
        background-color: #303030!important;
    }

    a {
        color: #9a9a9a!important;
    }

    a:hover {
        color: #9eeae7!important;
    }

    .alert-info {
        background-color: #4a4a4a!important;
        color: #fff!important;
        border-color: #000!important;
    }

    .dropdown-menu {
        background-color: #2f5371!important;
        border-color: #000!important;
        color: #fff!important;
    }

    .dropdown-menu > li > a {
        color: #fff!important;
    }

    .dropdown-menu > li > a:hover {
        color: #9eeae7!important;
        background-color: #4a4a4a;
    }

    .alert-warning {
        color: #fff!important;
        background: 0 0!important;
        border-color: #151515!important;
    }

    .navbar-default {
        background-color: #2f5371!important;
        border-color: #000!important;
    }

    .navbar-default .navbar-nav > li > a {
        color: #fff!important;
    }

    .navbar-default .navbar-brand {
        color: #fff!important;
    }

    .btn,
    .close {
        color: #fff!important;
    }

    ::-webkit-scrollbar {
        width: 7px;
        height: 7px;
    }

    ::-webkit-scrollbar-track {
        border-radius: 0;
    }

    ::-webkit-scrollbar-thumb {
        border-radius: 0;
        background: #008bff;
    }
}
