css:
/* Change the background color to a light gray */
body {
    background-color: #f6f6f6;
}

* {
  font-family: Verdana,sans-serif !important;
}

.ow_page_container {
    background: url(https://media.discordapp.net/attachments/1022262305179848755/1095447909656428635/Roblox_Background.png?width=1920&height=968) no-repeat;
    background-color: #000642;
}

/* Add a subtle drop shadow to the main content area */
main {
    box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.1);
}

.ow_site_panel {
    background: url(https://media.discordapp.net/attachments/1022262305179848755/1095454293680717864/Banner.png?width=1903&height=40) center no-repeat !important;
    z-index: 1000;
}

.ow_page_wrap {
    background: url(https://media.discordapp.net/attachments/1022262305179848755/1095375839740301332/image.png) !important;
}

.ow_bg_color {
    background: url(https://media.discordapp.net/attachments/1022262305179848755/1095375839740301332/image.png) !important;
}

.ow_box {
     background: url(https://media.discordapp.net/attachments/1022262305179848755/1095683083279282266/Background2.png?width=1000&height=1000) no-repeat;
     background-color: #FDFDFD;
}
.ow_content_menu li a {
    background: url(https://web.archive.org/web/20121222145037im_/http://www.roblox.com/images/cssspecific/rbx2/tab_white_31h_t2.png) no-repeat right top !important;
    height: 25px !important;
    padding: 6px 16px 0 8px !important;
    margin: 0 0 0 4px !important;
    color: #333 !important;
    font-family: Arial, Helvetica, sans-serif !important;
    font-size: 16px !important;
    font-weight: bold !important;
    display: block !important;
    text-transform: none !important;
}
.ow_content_menu li {
    margin: 0 !important;
    clip-path: none !important;
    box-shadow: none !important;
    position: relative !important;
    background: url(https://web.archive.org/web/20121222145037im_/http://www.roblox.com/images/cssspecific/rbx2/tab_white_31h_t1.png) no-repeat left top !important;
    background-position: left 0 !important;
    height: 31px !important;
    text-align: left !important;
    margin: 0 !important;
    z-index: 1 !important;
    position: relative !important;
}
.ow_content_menu li:hover {
    margin: 0 !important;
    clip-path: none !important;
    box-shadow: none !important;
    position: relative !important;
    background: url(https://web.archive.org/web/20121222145037im_/http://www.roblox.com/images/cssspecific/rbx2/tab_white_31h_t1.png) no-repeat left top !important;
    background-position: left -62px !important;
    height: 31px !important;
    text-align: left !important;
    margin: 0 !important;
    position: relative !important;
}
.ow_content_menu li a:hover {
    background: url(https://web.archive.org/web/20121222145037im_/http://www.roblox.com/images/cssspecific/rbx2/tab_white_31h_t2.png) no-repeat right top !important;
    height: 25px !important;
    padding: 6px 16px 0 8px !important;
    margin: 0 0 0 4px !important;
    color: #333 !important;
    font-family: Arial, Helvetica, sans-serif !important;
    font-size: 16px !important;
    font-weight: bold !important;
    display: block !important;
    background-position: right -62px !important;
}
.ow_content_menu li a span {
    background-image: none !important;
    background-position: none !important;
    background-repeat: none !important;
    padding: 0 !important;
    margin: 0 !important;
}
.ow_content_menu_wrap {
    margin: 0 !important;
}
td>div>p {
    display: block !important;
}
#table_id {
    position: relative !important;
    top: -3px !important;
    right: -1px !important;
    padding: 12px 12px 12px 12px !important;
    background: url(https://web.archive.org/web/20121222145037im_/http://www.roblox.com/images/cssspecific/rbx2/standardBox_01_bkg.png) top repeat-x #fff !important;
    border: 1px solid #aaa !important;
}
p {
    margin-bottom: 0px !important;
}
.game {
    border: 0px !important;
    padding: 0px !important;
    border-radius: 0px !important;
    background-color: none !important;
    margin: 8px !important;
}
.ow_ic_push_pin::before {
    content: none !important;
}
._memberads_list_tag {
    display: none !important;
}

/* i really want this to be global */
