css:
@-moz-document url-prefix(https://www.youtube.com/){
  /* Set the background image of the page */
  body {
    background-image: url("https://i.imgur.com/zi0a8Zx.jpg") !important;
    background-repeat: no-repeat !important;
    background-size: cover !important;
  }
  
  /* Change the font of the page */
  * {
    font-family: Arial, sans-serif !important;
  }
  
  /* Change the color of the top navigation bar */
  #masthead-container {
    background-color: #212121 !important;
    border-bottom: 1px solid #444 !important;
  }
  
  /* Change the color of the search bar */
  #search-input #search {
    background-color: #333 !important;
    border: none !important;
    color: #eee !important;
  }
  
  /* Change the color of the search button */
  #search-icon-legacy {
    background-color: #FF0000 !important;
    border: none !important;
    color: #fff !important;
  }
  
  /* Change the color of the video titles */
  #video-title {
    color: #eee !important;
  }
  
  /* Change the color of the video descriptions */
  #description-text {
    color: #aaa !important;
  }
  
  /* Change the color of the video thumbnails */
  #thumbnail {
    border: none !important;
    box-shadow: 0px 3px 10px rgba(0, 0, 0, 0.2) !important;
  }
  
  /* Change the color of the progress bar */
  .ytp-play-progress {
    background-color: #FF0000 !important;
  }
  
  /* Change the color of the video player controls */
  .ytp-chrome-controls {
    background-color: #333 !important;
    color: #eee !important;
    border-radius: 0px !important;
  }
  
  /* Change the color of the video player volume slider */
  .ytp-volume-slider-handle {
    background-color: #FF0000 !important;
  }
}
