css:
@-moz-document domain(reddit.com) {

body {background: #000 !important;  color: #ccc !important;}

img[id="header-img"]{width:0px !important; height:0px !important;border:0px solid #333 !important;padding-top:25px !important;padding-bottom:25px !important;padding-left:50% !important;padding-right:50% !important;background-color:#0a0a0a !important;background:  url("data:image/png;base64,") !important;background-repeat:no-repeat !important;background-position:50% 50% !important;-moz-border-radius:10px !important}

.roundfield{background-color:#000 !important}
.formtabs-content{border:none !important}
#sr-bar a{background-color:#000 !important;font-size:7pt !important}

a[id="sr-more-link"]{background-color:#000 !important;font-size:8pt !important}
.organic-listing{border:none !important}
img[alt="next"]{display:none !important}
img[alt="prev"]{display:none !important}
a[class="open"]{display:none !important}
.linkinfo{display:none !important}
.infobar{display:none !important}
.entry.unvoted{background-color:#000 !important}
.reddit-entry{padding:10px !important}
.help{background-color:#000 !important}
.tabmenu a{color:#fff !important;background-color:#000 !important;border:none !important}
a.bylink, span.option.active a, ul.flat-list.buttons > li > a{background:none !important}
.link{background-color:#000 !important;border:1px solid #333 !important; -moz-border-radius:25px !important;padding-left:15px !important;font-size:7px !important;width:98% !important;padding-right:15px !important;padding-top:3px !important;padding-bottom:3px !important}
.link.odd{background-color:#000 !important;border:1px solid #333 !important; -moz-border-radius:25px !important;padding-left:15px !important;font-size:7px !important;padding-top:3px !important;padding-bottom:3px !important}
.md{background-color:#000 !important;border:none !important}
.md p{color:#666 !important}
.md h2{background-color:#000 !important;color:#999 !important}
.nextprev{display:block !important}
.nextprev a{font-size:8pt !important}
iframe {background : #000 !important;}
a { text-decoration: none !important; font-weight: normal !important; color: #ccc !important; font-size:9px !important}
.expando{border:none !important;}
.expando-button{height:5px !important;width:5px !important;-moz-border-radius: 5px !important}
a:hover {text-decoration: none !important; color:#999 !important}
input[value="search reddit"]{-moz-border-radius:25px !important;padding-left:10px !important}
a.comments, a.author, a.hover {font-weight: bold !important; color:#999 !important}

a.title {text-decoration: underline !important; font-weight: bold !important; font-size:12px !important;}

.usertext-body a{color:#666 !important;}

.title {text-decoration: none !important;background-color:#000 !important;}

.thumbnail {display:none !important}
.rank {display:none !important}
.footer {display:none !important}
.footer-parent {display:none !important}



p.error{ background: #111 !important; color: #999 !important; font-size: 1em !important; font-weight: bold !important;}

.arrow{display:none !important;}
.midcol{display:none !important;}

#sr-header-area {background: #000 !important;}
#sr-header-area a {color: #999 !important; }
#sr-header-area span.title {color: #222 !important; }
#sr-header-area, #sr-header-area a.choice {color: #222!important; }
.raisedbox {background:#000 !important;}
.comment .child {border-left: 1px solid #aaa !important;}

#header-bottom-left  {background: #000 !important;}

#header-bottom-right {background: #000 !important;}

UL {background: #000 !important; -moz-border-radius: 6px !important; }

.midcol.unvoted  {background: #000 !important; -moz-border-radius: 10px !important; border: solid 1px #999 !important;}

.option.active.remove  {background: #000 !important; border: none !important}


#header {border: solid 0px #999 !important}


.sidebox.submit{display:block !important;background-color:#000 !important}
.sidebox.submit a{font-size:9pt !important;-moz-border-radius:15px !important}
.morelink{display:block !important;background-color:#000 !important;-moz-border-radius:15px !important}
.morelink a{color:#fff !important; background-color:#000 !important}
.sidebox.submit .spacer, .sidebox  {display: none !important}
.nub{display:none !important}
.sidebox.create{display:none !important}
.titlebox{display:none !important}
.sidecontentbox{display:none !important}
.spacer{display:block !important;background-color:#000 !important;}
.side{display:block !important;background-color:#000 !important;padding-top:0px !important; padding-right:12px !important}

.link.odd .entry {border: none !important}

.content {background: #000 !important}

.commentarea {background: #000 !important; color: #999 !important; -moz-border-radius: 10px !important;}

.drop-choices.lightdrop{background-color:#000 !important;font-size:10px !important}
.drop-choices.lightdrop a{font-size:9px !important}


}
