.dash-copyContainer {
    background: linear-gradient(to bottom, rgba(255, 255, 255, 0.9) 65%, rgba(255, 255, 255, 0.87) 70%, #fff 100%);
    border-radius: 5px;
    box-shadow: 0 1px 10px rgba(2, 2, 2, 0.14);
    flex: 1;
    overflow: hidden;
    padding: 15px;
    width: 654px;
    display: flex;
}
  .dash-letter.is-typed {
  opacity: 0; 
}
.dash-copy {
  position: relative;
  align-content: flex-start;
  color: #2E3141;
  display: flex;
  flex-flow: row wrap;
  font-family: "Calibri";
  font-size: 32px;
  font-weight: 400;
  width: 100%;
  transition: transform 250ms ease;
  flex: 1; 
}
.dash-copyContainer.is-single-line {
    align-items: center;
}
