css:
@-moz-document domain(edsby.com) {

@font-face {
  font-family: 'Cabin';
  font-style: normal;
  font-weight: 400;
  font-stretch: 100%;
  font-display: swap;
  src: url(https://fonts.gstatic.com/s/cabin/v26/u-4X0qWljRw-PfU81xCKCpdpbgZJl6XFpfEd7eA9BIxxkV2EH7alxw.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+0300-0301, U+0303-0304, U+0308-0309, U+0323, U+0329, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}




*{
font-family: 'Cabin', sans-serif;
}


html{
  background-color: #4a4a4a !important;
}


h1, h2, a, p,button, div:not(.xds-CalendarPanel-types-bubble),.xds-fa-glyph:not(.xds-CalendarPanel-types-bubble),.fa-graduation-cap ,.xds-polls-types-simplePollActionTitle, .FIBAssessment-type-glyph-icon_graduation_cap-icon{
  color: white !important;
}

.htmlprint-pageinner{
  background-color: gray !important;
}

.CartridgeView-master > div{
  background-color: rgba(0,0,0,0) !important
}

.xds-container:not(.xds-container-transparent){
border-radius: 20px;
background: #4a4a4a;
box-shadow: inset 10px 10px 20px #404040,
            inset -10px -10px 20px #545454;
  border: none !important;
 
  
  
}

.xds-button{
    border-radius: 10px;
background: linear-gradient(145deg, #196ac2, #1559a3) !important;
box-shadow:  5px 5px 10px #404040,
             -5px -5px 10px #545454;
  padding: 5px !important;
  border: none !important;
  color: white !important;
}

.xds-default-button{
    border-radius: 5px;
background: linear-gradient(145deg, #196ac2, #1559a3) !important;
box-shadow:  2px 2px 4px #404040,
             -2px -2px 4px #545454;
  padding: 6px !important;
  border: none !important;
  color: white !important;
}

.xds-button:hover,.xds-default-button:hover{

background: linear-gradient(145deg, #3483d7, #2c6eb5) !important;


}



body{
  background: #4a4a4a !important;
}

p{
  color: white !important;
}

.ClassBlock-actions-mywork-title{
  color: white !important;
}

.ClassList-classesContainer-classes-class-current, .xds-ClassList-link{
  border-radius: 20px;
background: linear-gradient(145deg, #434343, #4f4f4f) !important;
box-shadow:  10px 10px 20px #404040,
             -10px -10px 20px #545454;
  margin: 20px;
  padding: 20px !important;

}

.Header-banner{
  background-color: #4a4a4a;
}

.ClassBlock-class-details-info-code{
  color: white;
}

.ClassBlock-class-details-info-param{
  color: white !important;
}

.BaseGeneric-activity-title-text{
  color: white;
}

.FeedItemComponent {
    border-radius: 20px !important;
background: linear-gradient(145deg, #434343, #4f4f4f) ;
box-shadow:  10px 10px 20px #404040,
             -10px -10px 20px #545454;
  margin: 30px ;
  padding: 20px ;
  border: none !important;
}


.thread.FeedItemComponent {
  background: linear-gradient(145deg, #353434, #3f3e3e) !important ;
  margin: 50px !important;
  padding: 10px !important;
}

.thread.FeedItemComponent::before {
  display: none !important;
}






.scrollingNews-boxLayout-newsbox-item{
      border-radius: 20px;
background: linear-gradient(145deg, #434343, #4f4f4f) !important;
box-shadow:  4px 4px 8px #404040,
             -4px -4px 8px #545454;
  margin: 10px;
height: 170px;
  

}

.scrollingNews-boxLayout-newsbox-item-titleBlock{
  background-color: #4a4a4a;
}

.xds-feedEntry{

    border-radius: 20px;
background: linear-gradient(145deg, #434343, #4f4f4f) !important;
box-shadow:  10px 10px 20px #404040,
             -10px -10px 20px #545454;
}

.xds-container-transparent{
  background-color: rgba(0,0,0,0) !important;
}

.xds-sectionTitle{
  padding-top: 20px !important;
  padding-left: 20px !important;
  padding-right: 20px !important;
  font-family: 'Cabin', sans-serif !important;
}

.xds-CalendarPanel-event{
        border-radius: 20px !important;
background: linear-gradient(145deg, #434343, #4f4f4f) !important;
box-shadow:  5px 5px 10px #404040,
             -5px -5px 10px #545454;
    margin: 20px !important;
  padding: 10px !important;
}

.CalendarPanel-banner-left{
   padding-top: 20px !important;
  padding-left: 20px !important;
  padding-right: 20px !important;
  font-family: 'Cabin', sans-serif !important;
}

.CalendarPanel-default{
          border-radius: 20px !important;
