  border: none !important;
}

.xds-compoundFileThumbnail-smartThumb{
              border-radius: 20px !important;
  border: none !important;
background: linear-gradient(145deg, #434343, #4f4f4f) !important;
box-shadow:  5px 5px 10px #404040,
             -5px -5px 10px #545454;
    margin: 10px !important;
  padding: 10px !important;
}


.xdl-link,.FeedItemComponent-itembody-content-header-details-title-name-place,.xds-name{
  color: #5e9cff  !important;
}

.FeedItemComponent-itembody-content-header-details-title-name-place,.xds-name{
  font-weight: 900;
  font-size: 16px;
}

.ClassList-classesContainer-classes-recenttitle{
  background-color: rgba(0,0,0,0) !important;
  margin-left: 5px !important;
}





