css:
@-moz-document domain("killersports.com")
{
  table {
    width: 100%
  }

  table#tbHeader {
    display: none;
  }

  #sdql {
    padding: 1rem;
    font-family: monospace;
    font-size: 1rem;
    border-radius: 1rem;
    border: 2px solid silver;
    margin: 1rem;
  }

  #content > table > tbody > tr:nth-child(2) > td > table {
    font-size: 1.25rem;
  }

  #content > table > tbody > tr:nth-child(2) > td > table > tbody > tr:nth-child(1) {
    color: #0099ff;
  }

  #content > table > tbody > tr:nth-child(2) > td > table > tbody > tr:nth-child(2) {
    color: #38ba00;
  }

  #content > table > tbody > tr:nth-child(2) > td > table > tbody > tr:nth-child(3) {
    color: brown;
  }

  #content > table > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(1) {
    font-weight: normal !important;
    color: silver;
  }

  #content > table > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(2) {
    color: DodgerBlue;
  }

  #content > table > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(3) {
    color: deepskyblue;
  }

  #content > table > tbody > tr:nth-child(2) > td > img {
      height: 70px !important;
      float: right;
      opacity: 0.3;
      margin: 10px 10px 0 0;
  }

  #DT_Table > thead > tr > th {
    color: silver !important;
  }

  #DT_Table > tbody > tr > td {
    padding: 0.25rem;
    font-size: 1.25rem;
    font-weight: 400;
    color: #555;
  }

  #DT_Table > tbody > tr:hover > td {
    color: hotpink;
    background: lightyellow;
    cursor: pointer;
  }

  #content > table > tbody > tr {
    background: transparent;
  }

  #content > table > tbody > tr:nth-child(-n + 1) th {
    padding: 1rem;
    background-color: lightyellow;
    border: 2px solid gold;
    border-radius: 1rem;
    font-family: monospace;
    font-weight: 400;
    font-size: 1rem;
    color: mediumblue;
  }

  #content > table > tbody > tr:first-child > th {
    padding: 1rem;
  }

  #content > table > tbody > tr:nth-child(2) > td > table > tbody {
    padding: 1rem;
    border: 1px solid silver;
  }
}
