css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("linuxquestions.org"){

* {
background-color: #000000 !important;background-image: none !important;color: #999999 !important;border-color: #333 !important;}
a:link, a:active, a:visited {color: #999 !important;}
a:hover {color: lightgrey !important;}
.tcat{display:none !important}
.thead{display:none !important}


.navbar  a{display:inline !important}
#collapseobj_forumrules{display:none !important}
.tfoot{display:none !important}

img:not([title="Post New Thread"]):not([title="Reply"]):not([title="Reply With Quote"]):not([title="Edit/Delete Message"]):not([title="Go Back"]){display:none !important}
.navbar_notice{display:none !important}



img[alt="Edit/Delete Message"]{width:0px !important; height:0px !important;border:1px solid #333 !important;padding-top:10px !important;padding-bottom:10px !important;padding-left:30px !important;padding-right:30px !important;background-color:#0a0a0a !important;background:  url("data:image/png;base64,") !important;background-repeat:no-repeat !important;background-position:50% 55% !important;-moz-border-radius:10px !important}

img[alt="Reply With Quote"]{width:0px !important; height:0px !important;border:1px solid #333 !important;padding-top:10px !important;padding-bottom:10px !important;padding-left:30px !important;padding-right:30px !important;background-color:#0a0a0a !important;background:  url("data:image/png;base64,") !important;background-repeat:no-repeat !important;background-position:50% 60% !important;-moz-border-radius:10px !important}

img[alt="Reply"]{width:0px !important; height:0px !important;border:1px solid #333 !important;padding-top:10px !important;padding-bottom:10px !important;padding-left:50px !important;padding-right:50px !important;background-color:#0a0a0a !important;background:  url("data:image/png;base64,") !important;background-repeat:no-repeat !important;background-position:50% 75% !important;-moz-border-radius:10px !important}

img[alt="Post New Thread"]{width:0px !important; height:0px !important;border:1px solid #333 !important;padding-top:10px !important;padding-bottom:10px !important;padding-left:50px !important;padding-right:50px !important;background-color:#0a0a0a !important;background:  url("data:image/png;base64,") !important;background-repeat:no-repeat !important;background-position:center center !important;-moz-border-radius:10px !important}

}

@-moz-document url-prefix("http://www.linuxquestions.org/questions/newthread.php"){
.alt1 tr{display:block !important}
.alt1 tbody{display:block !important}
#vB_Editor_001{display:block !important}
img{display:block !important}
}

@-moz-document url-prefix("http://www.linuxquestions.org/questions/newreply.php"){
.alt1 tr{display:block !important}
.alt1 tbody{display:block !important}
#vB_Editor_001{display:block !important}
img{display:block !important}
}
