css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("google.com"){
*{
background-color: #000000 !important;background-image: none !important;color: #999999 !important;border-color: #333 !important;}

a:link, a:active, a:visited {color: #999 !important;}
a:hover {color: lightgrey !important;}
img[id="logo"]{display:none !important}
a[id="logo"]{display:none !important}
#fctr{display:none !important}
#sbl{display:none !important}
.gb3{display:none !important}

img[alt="Go to Google Videos home"]{display:none !important}
img[class="logo-img"]{display:none !important}
img[alt="Google News"]{display:none !important}
img[alt="Google Product Search"]{display:none !important}
img[alt="Gmail"]{display:none !important}
img[alt="Google"]{display:none !important}
.bubble{display:none !important}
.bottom-links{display:none !important}
#flp{display:none !important}
.div-footer{display:none !important}
.footer{display:none !important}

#mbEnd, #tads {display:none;}

input{font-size:9px !important; padding:0px !important;} 

input.lst[name='q']{height:10px !important; color: #fff !important; font-size:10px !important; padding-top:0px !important;}
input.lsb{height:20px !important; color: #fff !important; font-size:10px !important; padding-top:0px !important;}

input.lst, td{height:10px !important; background: transparent !important; padding:0px !important;line-height:1.1 !important;}

input.lsb[name='btnG']{padding-bottom:1px !important;}

.gac_m {font-size:11px !important; line-height:1.1 !important; margin:0 !important;}

.gac_t {font-size:11px !important;}

}

@-moz-document url-prefix(http://images.google.com){
input.lst[name='q']{height:15px !important; color: #fff !important; font-size:10px !important; padding-top:0px !important;}
}

@-moz-document url-prefix(http://news.google.com/newspapers){
*{
background:transparent !important;color: #000 !important;border-color: #111 !important;}
}



@-moz-document url-prefix(http://maps.google.com/){
*{
background:transparent !important;color: #000 !important;border-color: #111 !important;}
}
