css:
@-moz-document domain(www.iosoccer.com) {

/*General Site*/

text{
  fill:#FFFFFF !important
}

label{
  color:#FFFFFF !important;
}

#page-content {
    background-color: #292c31 !important;
}

.header__top-bar {
    background-color: #16171a !important;
}

.header__primary,.header__secondary, .header--layout-1 .header__primary-inner:before{
  background-color: #292c31 !important;
}

.nav-account .nav-account__item.has-children ul li a {
    color: #FFFFFF !important;
}

.nav-account .nav-account__item.has-children ul {
    background-color: #292c31 !important;
}

/*News*/
.posts__title a {
    color: #FFFFFF !important;
}
.text-muted {
    color: #FFFFFF !important;
}
.posts__item--card .post-author__name {
   color: #FFFFFF !important;
}

/*Text Editor (Used in Support Tickets)*/
.ql-snow .ql-fill, .ql-snow .ql-stroke.ql-fill, .ql-snow .ql-picker-label, .ql-snow .ql-stroke {
  fill: #FFFFFF !important;
  stroke: #FFFFFF !important;
  color:#FFFFFF !important
}
.ql-toolbar.ql-snow .ql-picker.ql-expanded .ql-picker-label, .ql-toolbar.ql-snow .ql-picker.ql-expanded .ql-picker-options {
    border-color: #FFFFFF !important;
}
.ql-snow .ql-picker-options {
    background-color: #343a40 !important;
    color: #FFFFFF !important;
}
.ql-editor.ql-blank:before {
    color: #FFFFFF !important
}

/*Profile Content Filter*/
.content-filter {
  background-color: #292c31 !important;
}

.content-filter__link, .content-filter__item--active .content-filter__link {
  color:#FFFFFF !important
}

/*Profile Stastistics Page*/
h4, h5, h6{
  color:#FFFFFF !important;
}

.player-profile-statistics__statistic-box__item-statistic
{
  color:#FFFFFF !important; 
}

.chart-legend .legend-label {
  color:#FFFFFF !important;
}

.chart-legend .legend-label .active .legend-label-text {
    color: #FFFFFF !important;
}

.chart-legend .legend-label:hover {
    color: #FFFFFF !important
}

.card{
  background-color:#343a40 !important;
  color:#FFFFFF;
}
.player-profile-statistics__statistic-box__item:nth-child(2n) {
    background-color: #3b4248 !important;
}

.statistic-spotlight-icon.icon-soccer-shots {
  opacity: 0.05
}

/*Page Navigation*/
.ngx-pagination a, .ngx-pagination button,.ngx-pagination .disabled {
    color: #ffffff !important;
}

.ngx-pagination .current {
    background-color:#2199e8 !important;
}

.ngx-pagination li{
  background-color:#343a40 !important;
}

.py-4 {
    background-color: #292c31 !important;
}

/*Tournament Brackets*/
.tournament-bracket__team, .tournament-bracket__team--champ,  .tournament-bracket__team--winner {
	background-color:#343a40 !important
}

.tournament-bracket__team-desc {
	color:#FFFFFF  !important
}

/*Tables*/
.table, .table thead th{
  color:#FFFFFF !important;
}

.team-result-filter__item select.form-control {
    background-image: url(https://i.imgur.com/4Rar2Hz.png) !important;
}

.team-result-filter__item, select.form-control {
  color:#FFFFFF !important
}

.team-result--score {
    background-color: #6c757d !important;
  	color:#FFFFFF !important;
}

.table-hover tbody tr:hover td {
    background:#3b4248 !important;
    color:#FFFFFF;
}

.table-hover>tbody>tr {
    transition: none !important
}

/*Buttons*/
.btn-light:not(:disabled):not(.disabled).active, .btn-light:not(:disabled):not(.disabled):active, .show>.btn-light.dropdown-toggle{
  color:#FFFFFF !important;
  background-color:#3b4248 !important;
  border-color:#FFFFFF  !important;
}

.btn-light {
    color: #FFFFFF !important;
    background-color: #6c757d !important;
    border-color: #f8f9fa !important;
}

.btn-primary {
    color: #fff !important;
    background-color: #31404b;
}

/*Dropdowns*/
.dropdown-menu {
  background-color:#343a40 !important;
  color:#FFFFFF !important
}

select.form-control option {
    background-color:#343a40 !important ;
    color: #FFFFFF !important;
}

.form-control{
  background-color:#343a40 !important;
    color:#FFFFFF !important
}

.form-control:focus {
  background-color:#6c757d;
    color:#FFFFFF !important
}

.checkbox .checkbox-indicator {
  background-color:#6c757d !important
}

select.form-control{
  background-color:#343a40 !important
}

/*Calendar*/
.mat-datepicker-content {
  background-color:#343a40;
  color:#FFFFFF !important;
}
.mat-calendar-body-cell-content, .mat-calendar-body-label {
  color:#FFFFFF !important
}

.mat-calendar-arrow {
    border-top-color:#FFFFFF !important;
}

.mat-datepicker-content .mat-calendar-next-button, .mat-datepicker-content .mat-calendar-previous-button, .mat-datepicker-toggle {
    color: #FFFFFF !important
}

.form-control-sm, .input-group-sm>.form-control, .input-group-sm>.input-group-append>.btn, .input-group-sm>.input-group-append>.input-group-text, .input-group-sm>.input-group-prepend>.btn, .input-group-sm>.input-group-prepend>.input-group-text {
    background: #343a40 !important;
  color: #FFFFFF !important
}

.mat-calendar-table-header {
    color: #FFFFFF !important;
}

/*Live Scores / Results*/
li.commentary__item:nth-child(2n) {
    background-color: #3b4248 !important;
}

.commentary__item>strong {
    color: #FFFFFF !important;
}

.game-timeline__time {
   background-color: #343a40 !important;
  color:#FFFFFF !important
}

.game-timeline__event-name {
    background-color: #FFFFFF00 !important;
  color:#FFFFFF !important
}

.commentary__time {
    color: #FFFFFF !important;
}
  
.c100:after {
  background-color:#343a40 !important
}

.circle-value {
  color:#FFFFFF !important
}

.icon-svg:before, .no-svg .icon-svg:before {
    background-image: url(https://i.imgur.com/DuIW7sb.png) !important;
}

}
