css:
@-moz-document url-prefix("https://userstyles.org") {
html, #nav-view-responsive {background-color: white!important; min-width:1200px;}

body {width:1100px; min-height:1100px; display: block; margin-right: auto; margin-left: auto; background-color: white;}

.style_category_card_wrapper {border: 1px solid gray; box-shadow: none; border-radius: 0px; width:210px;}

.style_category_card_wrapper:hover, .style_category_card_wrappe:hover {box-shadow: none;}

.pagecontent, #container-responsive {min-height: 100%!important;}

.NotificationLine {width:1100px; }

.NotificationLine-Action {height:40px; width: 100px;}

.bannerWrapper {margin-top:20px!important}

.adContainer {position: relative; display: none;}

.us-header {background-color: rgb(255,232,139) !important; border-radius: 5px!important; display: block; margin:9px; position: absolute!important; height:35px!important;}

.us-header > .right div {border-radius: 0px !important; height: 30px!important;}

#create_new_style {display: none!important;}

.hamburger {display: none;}

#subscription_link svg {margin-top:-4px}

.top_logo {margin-right:5px;margin-left:40px;}

.header-search {background-color: white; border: 1px solid gray; height:20px; width:270px; font-size: 10px; margin-top:13px; margin-left: 70px;}

.header-search, .type_search, .input_text_search, .type_search_select, .type_search_option:hover {border-radius: 0px !important}

.type_search_select {width:150px!important;}

.type_search {height:20px!important;}

.type_search_option {height:20px!important; padding: 5px 10px 5px!important;}

.input_text_search {background-color: white!important; color: black!important; width:30px; margin-left:-5px!important;}

.us-header .right div{background-color: black;}

.navigation {background-color:white; box-shadow: none; width:200px; position: absolute; height:1000px; margin-top: 30px!important; float:left;}

.navigation div {color: black;}

.navigation a, .navigation-link {box-shadow: 0px!important}

.navigation-category {margin-top:8px!important; color: black!important; display: none;}

.links-box {border-radius: 5px!important; background-color: rgb(209,236,236); box-shadow: none!important; margin-bottom: 5px;}

.navigation-link {height:10px; text-decoration: underline; border-left: none!important; font-size: 10px!important; border: none!important; box-shadow: none!important}

.links-box a:hover , .navigation-link:hover {background-color: none!important;}

.links-box img {display: none;}

.us-category--narrow, .stylecards__container, #title_div {width:900px; padding: 0px;}

#title_div h1 {margin-bottom:5px;}

#view-responsive {margin-top:-35px;}

div[class="view_inner HomePage"] {width: 900px; margin-left: 100px!important;}

div[class="view_inner StylesCategory"] {margin-left: 300px!important;}

.us-category__title, .us-stylecards__container {width:900px!important;}

#switch_view_buttons {display: block!important; margin-left: auto!important; margin-right: auto!important; float: right;}

#switch_view_buttons img {width: 30px; height: auto;}

.us-stylecards__container {width:900px!important; padding: 0px;}

div[class="sc-bdVaJa fzKkUK"] {display: none}

.us-stylecard--short, .us-stylecard--short:hover, .us-stylecard--long {box-shadow: none;}

.us-category__title {width: 890px!important; display: block!important;}

.us-category__title > div {width: 750px!important;}

.us-button {display: none!important;}

.us-title {text-decoration:underline; color: rgb(34,67,117)}

.us-stylecard--short {height:260px!important}

.us-stylecard--long {margin-bottom:-20px!important;}

.premium-container:empty {display: none;}

.fallbackDiv, .fallbackDiv .fallbackImg {width: 850px!important;}

#install_stylish_style_button, .css_button {border-radius: 0px; box-shadow: none;}

#style_info {margin-left: 20px; width: 700px;}

.login-page {margin-top:20px;margin-left:50px!important;}

#archive_style_button {box-shadow: none}

.view_inner {width:1000px; margin-left: 160px;}

.view_inner iframe {width:1200px; margin-left:-5px; margin-top:20px}

div[class="comment-content edit"] {padding:10px; min-height:40px;}

div[class="comment-content edit"] .content-wrapper {margin-top:-9px; min-height: 20px;}

.comments-wrapper {margin-top:10px;}

.comment-wrapper {min-height:15px;}

.comment-content {margin-bottom: -10px!important; padding: 0px 5px; width:100%!important;}

.comment-wrapper .top, .comment-wrapper .content {float:left; margin-right: 10px; margin-bottom:0px;}

div[class="actions with-padding"] {padding-left:5px!important; clear:both; margin-top: 5px;}

div[class="actions with-padding"] .right-actions {margin-top: 10px;}

.comment-wrapper .actions {margin:15px 0px; width:160px!important;}

.replies-wrapper, .replies-wrapper .comment-content  {margin-top: 20px;}

.replies-count-wrapper {margin-bottom:10px; margin-left:15px; margin-top:-5px; display: block!important;}

.actions .left-actions {height: 15px;}

.username {margin-right: 5px!important;}

.show-more-wrapper {clear: both!important;}

.show-more-button {border-radius: 0px!important; margin-top:15px;}

.actions .left-actions:empty, .actions .right-actions:empty {display: none;}

.PageContent > p {width:850px;}

.div-arrow {box-shadow: none!important; border: none!important}

#footer {padding:10px 0px; background-color: white; margin-bottom: 0px!important;}

.footer_link {margin-right:-8px; text-decoration: underline; font-size:12px;}

.footer-inside {margin: -20px;}

#rightFooter svg {display: none}

.bottomLinks {margin-top:5px;}

#appstores-links {margin-right:0px!important; margin-top:8px!important;}

#appstores-links a {margin-left:2px!important;}
}
