css:
@-moz-document domain("horizonxi.com") {
.page-background, .page-body, body, .nav-wrapper, .datatable, .dt-table, input, .list, .player
{
  background-color: #323232 !important;
}

.card, .row {
  background-color: #374450 !important;
}

.player {
  border: solid;
}

table, h1, h2, h3, p, .row, dt, dd, .seacom-msg  {
  color: white !important;
}

.subjob-cap{
  color: #50b4d6 !important;
}

.level-cap{
  color: #deb41e !important;
}

/* unvisited link */
a:link, .link, .nav-wrapper::after, .nav-wrapper > a:link
{
  color: #dfaf8f !important;
}

ul > li > a:link
{
  color: #dfaf8f !important;
}

/* visited link */
a:visited, .text:visited{
  color: #8c8cbc;
}

/* mouse over link */
a:hover, .wds-button.wds-is-text:hover, .text:hover {
  color: #9c9ccc;
}

/* selected link */
a:active {
  color: #ffd7a7;
}

/* mouse over table link */
table a:hover {
  color: hotpink;
}

/* selected table link */
table a:active {
  color: lightcyan;
}


}
