    content: "";
}

selector {
    top: -2px;
    left: -8px;
}

/* On ___________*/

[aria-label="MUTE"] svg
{
	background: url(https://i.ibb.co/xzPf8xZ/red-volume-on.png) no-repeat!important;
	box-sizing: border-box;
}

[aria-label="MUTE"] svg:hover
{
	background: url(https://i.ibb.co/CQr6dDV/red-volume-on-hover.png) no-repeat!important;
	box-sizing: border-box;
}

/* Off ___________*/

[aria-label*="Unmute"] svg
{
	background: url(https://i.ibb.co/H7Vt7m1/red-volume-off.png) no-repeat!important;
	box-sizing: border-box;
}

[aria-label*="Unmute"] svg:hover
{
	background: url(https://i.ibb.co/GRKNpS2/red-volume-off-hover.png) no-repeat!important;
	box-sizing: border-box;
}

[aria-label="MUTE"] svg,
[aria-label="MUTE"] svg:hover,
[aria-label*="Unmute"] svg,
[aria-label*="Unmute"] svg:hover
{
	background-size: 35px!important;
	height: 36px!important;
	padding-left: 36px!important;
	width: 1px!important;
}


}

