/*淡蓝色样式*/

a {
	-webkit-transition: all 0.3s ease-out;
}
a:hover {
	color: #39F !important;
	text-shadow: -5px 3px 18px #39F !important;
	-webkit-transition: all 0.3s ease-out;
}
*::-webkit-selection {
	background: #333333 !important;
	color: #00FF00 !important;
}

