css:
@-moz-document domain("youtube.com") {
  /* Custom background with a GIF */
  body {
    background-image: url("https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/ffa4ad00-3340-4946-bb16-e9603d023778/dcr9l12-980872e3-3622-4976-ab25-c62638c6b91f.gif?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..PGcCWJBZrBV1N_XwER-BopMU55Ta3NrQhXf-nN6TEd4");
    background-repeat: repeat;
    background-color: #1c1c1c;
  }

  /* Dark blue color palette */
  :root {
  ´yt-primary-color: #3b3b98;
  ´yt-secondary-color: #4a4a4a;
  ´yt-tertiary-color: #1c1c1c;
  ´t-text-color: #e0e0e0;
}

  /* Compact layout */
  #masthead-container, #secondary, #comments, #related, #comments, #subscribe-button {
    display: none !important;
  }
  #content, #primary, #primary-inner {
    margin: 0 !important;
    padding: 0 !important;
  }
  #page-manager, #content, #content-container, #primary, #primary-inner {
    width: 100% !important;
  }
  #player-theater-container, #player-theater-container > #player-theater {
    width: 100% !important;
    height: calc(100vh - 56px) !important;
  }
  
  /* Simple buttons */
  #buttons ytd-button-renderer, .ytp-chrome-controls {
    display: inline-flex !important;
    align-items: center !important;
    justify-content: center !important;
    margin: 0 4px !important;
    padding: 6px !important;
    border-radius: 50% !important;
    background-color: var(--yt-primary-color) !important;
    box-shadow: none !important;
    border: none !important;
    font-size: 14px !important;
    color: var(--yt-text-color) !important;
  }
  #buttons ytd-button-renderer:hover, .ytp-chrome-controls:hover {
    background-color: var(--yt-secondary-color) !important;
  }
  #buttons ytd-button-renderer:focus, .ytp-chrome-controls:focus {
    outline: none !important;
  }
  #buttons ytd-button-renderer .yt-icon, .ytp-chrome-controls .ytp-svg-fill {
    fill: var(--yt-text-color) !important;
  }
}
