css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.fullcodepress.com/") {
body {
background-color: #252d5b !important;
background-image: none !important;
font-family: "Trebuchet MS", "Lucida Grande", "Lucida Unicode", Georgia, sans-serif !important;
color: #000 !important;
padding-top: 8px !important;
}
h2, h3, legend {
color: #333 !important;
}
#container {
width: 935px !important;
background-image: none !important;
margin-left: 35px !important;
}
#header {
float: none !important;
width: auto !important;
min-height: 220px !important;
padding: 0 0 0 0 !important;
background-color: #fff !important;
background-image: url(http://farm1.static.flickr.com/78/199427224_4d86a522b8.jpg) !important;
background-position: -110px 8px !important;
background-repeat: no-repeat !important;
border-bottom: 8px solid #fff !important;
border-left: 8px solid #fff !important;
}
#header h1, #header p {
float: none !important;
width: auto !important;
margin-left: 180px !important;
position: relative !important;
background-color: #fff !important;
}
#header h1 {
padding: 12px 2em 2em 40px !important;
}
#header p {
padding: 0 1em 2em 40px !important;
}
#header h1 img {
display: none !important;
}
#header h1:before {
content: "Photos: http://www.flickr.com/photos/becs027/199427224/ \A               http://www.flickr.com/photos/becs027/35313635/";
color: #333;
font-size: 0.4em;
font-weight: normal;
display: block;
white-space: pre;
}
#header h1:after {
content: "Full Code Press International Site in a Day Competition, 2007.";
color: #281942;
display: block;
padding-top: 1.4em;
}
#innercontainer {
background-image: url(http://farm1.static.flickr.com/21/35313635_027e3851a4.jpg) !important;
}
#innercontainer, #innercontainer2 {
float: none !important;
width: auto !important;
margin-left: 0 !important;
padding-bottom: 0 !important;
display: block !important;
background-position: right top !important;
background-repeat: no-repeat !important;
background-color: #ccc !important;
border-top: none !important;
}
#nav ul:after {
content: "."; 
display: block; 
height: 0; 
clear: both; 
visibility: hidden; 
}
#nav ul {
padding: 1em 0 0.4em !important;
}
#nav a span {
display: none!important;
}
#nav li a {
width: auto !important;
height: auto !important;
}
#nav a {
color: #eee !important;
text-decoration: underline !important;
font-size: 1.2em !important;
padding: 0 1em 0.2em !important;
}
#nav a:hover, #nav a:focus {
text-decoration: none !important;
}

#content {
float: none !important;
width: 385px !important;
background-color: #fff !important;
padding: 2em 35px 2em 20px !important;
border-top: 16px solid #252d5b !important;
}

#extras {
float: none !important;
background-color: #fff !important;
width: 385px !important;
}
#innercontainer #extras {
padding: 0 35px 1em 20px !important;
}
#innercontainer2 #extras {
padding: 250px 35px 1em 20px !important;
background-position: 40px 0px !important;
}
#footer {
background-color: #333 !important;
background-image: url(http://farm1.static.flickr.com/49/178164688_52145f44e6.jpg) !important;
background-position: right -30px !important;
text-align: left !important;
width: 383px !important;
padding: 260px 535px 3em 20px !important;
color: #fff !important;
}
#footer a {
color: #fff !important;
}
}

@-moz-document url("http://www.fullcodepress.com/about/") {
body {
background-color: #262c12 !important;
}
#header h1:before {
content: "Photos: http://www.flickr.com/photos/becs027/199427224/ \A               http://www.flickr.com/photos/becs027/563803298/";
}
#content {
border-top: 16px solid #262c12 !important;
}
#nav #about a {
background-color: #4f432b !important;
}
#innercontainer2 {
background-image: url(http://farm2.static.flickr.com/1038/563803298_63fb5439ce.jpg) !important;
}
}

