css:
@-moz-document domain(000webhost.com){
:root{
	--bg-1:#202124;
	--bg-2:#353535;
	--bg-3:#888;
  --color-1:#eee;
  --color-2:#000;
}
body, .form-control{background:var(--bg-2) !important; color:var(--color-1);}

body .spinloader__bg{background:#353535b3}

.header-container, #main-header, .et_mobile_menu 
 /*header || najjaśniejszy kolor */{background:var(--bg-3);}  
.app-list__header,.bg-white,.bg-white-two, #main-content, .select-kit-body, 
.login-left-side, .login-left-side *, .modal-inner-container *, .topic-map
 /* tło */{background:var(--bg-2) !important;}
#nav-container,.user-menu__container,.et_pb_section, .combo-box-header,
.dashboard-quick-actions__card, .domain-add__card-wrapper, 
.table-striped>tbody>tr:nth-of-type(odd),.intercom-popup-holder--white, 
.plan-section-table__row, .cart-card, .compare-table-card__popular--hidden, 
.compare-table-card, .compare-table-card__popular--hidden, .dash__card, 
.app-box__info, .pricing-table-v4__column, .generic-trusted-by-v2, 
.nps-wizzard__panel, .et_pb_blog_grid .et_pb_post,  .select-kit-row.is-highlighted
 /* box || najciemniejsze */{	background:var(--bg-1) !important;}
.table-bordered>thead {background-color:rgb(255 255 255 / 6.8%);}
.dash__card/*cień box-a */{
box-shadow:0 5px 10px rgba(154,160,185,.05), 0 15px 40px rgba(166,173,201,.2);}
h1,h2,h3,h4,h5,h6,table, tr, td,input,
.dashboard-quick-actions-card__name, .text-gray, #topic-title h1 a,.topic-count, 
.intercom-popup-holder--white, .bg-white, .text-charcoal-grey, .cart-card, 
 .plan-section-table__row, .cart-card__body, .topic-list .main-link a.title, 
.compare-table-card__price-block, .compare-table-card__features-block--row, 
.intercom-popup-holder__description, .app-box-holder__app-name, 
.app-box-holder-status-section__text, .paragraph, .et_pb_text_0 h1, 
.language-picker-toggle, .heading, .square-grid__square, .combo-box-header, 
form.et_pb_searchform input[type=text], .topic-list .topic-excerpt,
table th, .btn-flat.btn.modal-close svg
{color:var(--color-1) !important;}
/*hiperłącza */
a-0
{ color:#8ab4f8 !important;}
.dash-button--full, .button--secondary, #top-header a, #main-header a, 
.et_pb_search input.et_pb_searchsubmit, .generic-navigation-v3__holder a, 
.pagination.clearfix a, sidebar a, .post_meta_top #breadcrumbs a
{ /*hiperłącza inne */
	color:inherit  !important;
}

 /* ___ */
.app-list__create-button{
  background:#6747c7;
  color:var(--color-1);
}


form.et_pb_searchform input[type=submit].et_pb_searchsubmit, .names span a, 
.post-info.edits .widget-button .d-button-label, .topic-meta-data .post-info a, 
.post-info.edits .widget-button .d-icon, 
.timeline-container .topic-timeline .start-date, 
.timeline-container .topic-timeline .timeline-ago, .names .user-title, .names .fa, 
.timeline-container .topic-timeline .now-date
{/* ciemny tekst */
   color:var(--color-2) !important;
   font-weight: bold;
}

.modal-footer:before{background:
  linear-gradient(to bottom,#0000,var(--bg-2));}

.table-bordered>tbody>tr>td, .table-bordered>tbody>tr>th, .table-bordered>tfoot>tr>td, .table-bordered>tfoot>tr>th, .table-bordered>thead>tr>td, .table-bordered>thead>tr>th {
  border:1px solid #888;
}


 /*przezroczystość - dla kupiena */
.upgrade-section__header-title{
  opacity:0.6;
}


 /*rodzdzielenie flex - dla kupiena  */
.compare-section__comparison{}



 /*wyszarenie funkcji pro */
.dashboard-quick-actions__card--premium .dashboard-quick-actions-card__icon{
    filter:brightness(99) grayscale(1);
}

 /*home - ico */
.dashboard-quick-actions__card--premium .dashboard-quick-actions-card__icon{
    filter:grayscinvertale(1);
}
}
