css:
@-moz-document url("https://720pizle5.com/") , domain("720pizle5.com")
{
body {
    background:#000 !important;
}
#baymovie-manset {
    background:#000 !important;
}
#baymovie-manset-ic {
    background:#111 !important;
}
#header {
    background:#111 !important;
    border:none !important;
}
.logo a {
     display:none !important;
    height:0px !important;
    width:0px !important;
}
#header /*.header-ic*/ .logo  {
    background-image: url("https://i.ibb.co/jLBPbk5/logo.png") !important;
    height:45px !important;
    width:151px !important;
    /*margin-top:-5px !important;*/
}
#footer .footer-ic .logo img {
  float: none
}
#footer .footer-ic .logo {
      background-image: url("https://i.ibb.co/jLBPbk5/logo.png") !important;
  width: 150px;
}

#header .header-menu .arama-kutusu {
    background: #333 !important;
    color: #fff !important;
    -webkit-box-shadow: 0px 0px 5px #111 inset !important;
    box-shadow: 0px 0px 0px #111 inset !important;
    color:#fff !important;
    border:none !important;
    height:28px !important;
}
#header .header-menu .dvd-cikis {
    background: #333 !important;
    color: #666 !important;
    height:26px !important;
}
#header .header-menu .dvd-cikis a {
    color: #aaa !important;
}
.kayit-ol, .giris-yap, .kayit-yap-buton, .uye-ismi, .cikis-yap {
    background:#333 !important;
    transition: background ease 0.3s !important;
    height:28px !important;
}
.kayit-ol a, .giris-yap a, .uye-ismi a, .cikis-yap a {
    color:#aaa !important;
    transition:all linear 0.2s
}
.kayit-ol:hover, .giris-yap:hover, .uye-ismi:hover, .cikis-yap:hover {
    background:#222 !important
}
.kayit-ol:hover a, .giris-yap:hover #giris-yap, .uye-ismi:hover a, .cikis-yap:hover a {
    color:#ddd !important
}
.manset-tablo {
    background:#222 !important;
    border:none !important;
}
.manset-tablo a {
    color:#ccc !important;
}
/*------------------------Giris-Panel-----------------*/
 .giris-yap input {
    background:#222 !important;
    box-shadow:0px 0px 5px #111 inset !important;
    -webkit-box-shadow:0px 0px 5px #111 inset !important;
    border:none !important;
}
.giris-yap label {
    color:#aaa !important;
}
.header-giris-paneli form {
    background:#333 !important;
}
#ileri {
    background:url("https://i.hizliresim.com/9Yrd5N.png") !important;
    background-size: 100% !important;
}
#geri {
    background:url("https://i.hizliresim.com/DO5d9o.png") !important;
    background-size: 100% !important;
}
#ana {
    background:none !important;
}
/*-------------------------Kayit-Ol--------------------*/
 #kayitol .header-kayit-paneli form {
    background:#111 !important;
    color:#ccc !important
}
#kayitol .kayit-yap-buton {
    line-height:0px !important;
}
#kayitol input {
    color:#ccc !important;
}
.sag select {
    background:#333 !important;
}
/*-------------------------Menu------------------------*/
 #header-alt-menu {
    border:none !important;
    background:#111 !important;
    border-radius: 6px !important;
}
.header-menu-sol li {
    border:none !important;
    height: 35px !important;
}
.header-menu-sag li {
    border:none !important;
    height: 35px !important;
}
#header-alt-menu li a {
    color:#aaa !important;
    line-height:50px !important;
    transition:all linear 0.2s
}
#header-alt-menu li a:hover {
    color:#ddd !important;
    background:#111 !important;
    box-shadow:0px 0px 5px #111 inset !important;
    -webkit-box-shadow:0px 0px 5px #111 inset !important;
}
.golge {
    box-shadow: 0 0 6px rgba(0, 0, 0, 0.8);
}
.xs-m-10 {
    background: #111 !important;
    border: #111 !important;
    color: #fff !important;
    margin: 0px !important;
}
/*---------SideBar--------*/
 .sag-tablo-arama-rastgele a {
    color:#aaa !important;
    background:none !important;
    transition:all linear 0.3s
}
.sag-tablo-arama-rastgele a:hover {
    color:#ccc !important
}
#sag-tablo-kategoriler .baslik {
    background: none !important;
    color:#aaa !important;
}
#sag-tablo-kategoriler {
    box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.6) !important;
    -webkit-box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.6) !important;
    border-radius: 6px;
}
#sag-turkce-yabanci-kategori {
    background:#111 !important;
}
#sag-turkce-yabanci-baslik li {
    background:none !important;
    transition:all linear 0.2s
}
#sag-turkce-yabanci-baslik li a {
    color:#aaa !important;
    transition:all linear 0.2s
}
#sag-turkce-yabanci-baslik li a:hover {
    color:#ddd !important;
}
#sag-turkce-yabanci-baslik li.kategori-aktif {
    background:#111 !important;
    border-radius: 6px 6px 0px 0px !important;
}
#sag-turkce-yabanci-baslik li.kategori-aktif a {
    color:#ddd !important
}
/*-------------------Film-Content--------------*/
 .film-kategori-oyuncu-biyografi-ic {
    background:#333 !important;
    color: #ccc !important;
}
#film-icerik-tablo .film-baslik h1, .film-kategori-oyuncu-biyografi-baslik h1, #film-icerik-tablo .film-baslik h2, .film-kategori-oyuncu-biyografi-baslik h2 {
    color:#ccc !important
}
.film-icerik-izle .izle {
    color:#bbb !important;
    background:#222 !important
}
.film-icerik-izle .izle:hover {
    background:#111 !important;
    color:#ddd !important;
}
.film-kategori-oyuncu-biyografi-sag li span {
    color:#dae !important
}
.izle-menu a {
    color:#aaa !important
}
.izle-menu a:hover {
    background:#111 !important;
    color:#ddd !important;
}
#film-icerik-tablo .film-baslik
{
    background-color: #111 !important;
}
/*------------------Film-------------------------*/
 #kendisi {
    display:block !important;
}
#film-icerik-tablo .film-icerik-tab {
    background:none !important
}
#film-icerik-bilgiler .bilgi-tablo-2, #mesajlar-tablo .mesaj-tablo-sag {
    background:#111 !important
}
#mesajlar-tablo .mesaj-tablo-cevap-sol {
    background:#333 !important
}
#yorumsuz {
    color:#dae !important;
}
/*--------------------Kategoriler-----------------*/
 #sag-turkce-yabanci-kategori ul li a {
    background:#222 !important;
    color:#aaa !important;
    transition:all linear 0.2s;
}
#sag-turkce-yabanci-kategori ul li a:hover {
    background:#333 !important;
    color:#ddd !important;
}
#sag-turkce-yabanci-kategori ul li .tumunu-gor {
    color:#ccc !important;
}
.sag-tablo-kategori-filmler
{
    background-color: #111 !important;
}
/*-----------------------Footer-------------------*/
 .pagination ul > .active > a, .pagination ul > .active > span {
    background:none !important;
    color:#ddd !important;
}
.pagination ul > li > a, .pagination ul > li > span {
    background:none !important;
    color:#aaa !important;
}
.pagination ul > li > a:hover {
    color:#ddd !important;
}
#footer {
    box-shadow: 0px 0px 5px #111;
    -webkit-box-shadow: 0px 0px 5px #111;
}
.footer-alt {
    background:#111 !important;
    color:#ccc !important;
}
.footer-ic {
    background:#111 !important;
}
/*---------------------Removed----------------*/ 
.sag-tablo-e-bulten, #backkapat, #pageskinurl img, .adv-728, .alert-info, .reklamlink, .reklamarkaplan, 
  #reklamarkaplan, #reklami, #film-icerik-bilgiler {
    display: none !important;
    height: 0px !important;
    width: 0px !important;
}
/*-------------------Webkit-Scroll--------------*/
 .uiScrollableArea {
    background-color: #303030 !important;
}
::-webkit-scrollbar {
    width: 9px !important;
    height: 9px !important;
}
::-webkit-scrollbar-track-piece {
    background-color: #303030 !important;
    background: #303030!important;
}
body::-webkit-scrollbar-track-piece {
    background-color: #222 !important;
}
::-webkit-scrollbar-track-piece:no-button {
}
::-webkit-scrollbar-thumb {
    background-color: #444 !important;
    border-radius: 3px !important;
}
::-webkit-scrollbar-thumb:hover {
    background-color: #777 !important;
}
::-webkit-scrollbar-thumb:active {
    background-color: #666 !important;
}
#mv-tiles {
    text-align:center !important;
    width: 740px !important;
}
}
