css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url(http://morfix.mako.co.il/),
url(http://morfix.mako.co.il/Default.aspx),
url(http://morfix.mako.co.il/default.aspx) {

body {background:#666 url("http://i20.photobucket.com/albums/b236/paganmedia/morfixbg.jpg") no-repeat top center !important;}
table {width:250px !important;}
td[align=left] {display:none !important;}
td[align=right] {width:250px !important; text-align:center !important;}
iframe {display:none !important;}
span {margin:auto !important;}
body, a, a:visited {color:#aaa !important;}
span {color:#888 !important;}
.copyright {display:none !important;}
img[align=right] {display:block !important; margin-right:110px !important;}
span {float:none !important;}
#searchBox {width:300px !important; font-size:18px !important; border:3px solid #000 !important; background: #000 !important;}
nobr>img {display:none !important;}
#tdQueryStrip {display:none !important;}
tbody {display:block !important;}
form {width:320px !important; display:block; margin:auto; padding-top:100px;}
nobr>a {display:none !important;}
div>span {display:none !important;}
td.tdQueryStrip {border:0px !important;}
span {padding:0px;}
#ButtonTranslate {position:relative; top:50px; right:185px; width:0px !important; height:32px !important; padding-left:77px !important;background: transparent url("http://i20.photobucket.com/albums/b236/paganmedia/transub.png") !important;}
#IMG1 {width:0px !important; height:80px !important; padding-left:245px !important; background: url("http://i20.photobucket.com/albums/b236/paganmedia/morfix.png") !important;}
}
