css:
@-moz-document url("https://www.youtube.com/feed/history") , domain("youtube.com") {
#progress.ytd-thumbnail-overlay-resume-playback-renderer {
    background-color: rgba(250, 0, 0, .80);
    height: 100%;
    width: 100%;
}

ytd-thumbnail-overlay-resume-playback-renderer {
    background-color: rgba(0, 0, 0, .35);
    height: 100%;
    width: 100%;
}

#progress.ytd-thumbnail-overlay-resume-playback-renderer:before {
   text-shadow: 2px 2px #000000, -2px -2px #000000, -2px 2px #000000, 2px -2px #000000;
    padding-top: 20%;
    content: "WATCHED";
    font-size: 30px;
	text-align: center;
    position: absolute;
    left: 0;
    width: 100%;
    height: 188.5px;
}

yt-formatted-string.ytd-thumbnail-overlay-playback-status-renderer {
    display: none;
}
ytd-thumbnail-overlay-toggle-button-renderer, ytd-thumbnail-overlay-time-status-renderer[overlay-style=DEFAULT] {
    z-index: 2;
}
}
