css:
@-moz-document domain('web.snapchat.com'){
body{
/*Applies to the chat window*/
 /*Background for text enter box*/
  --sigObject:#000;
 /*Border for enter text box*/
  --sigObjectDown:#222;
 /*Image highlight*/
  --sigBackgroundSecondary:transparent;
 /*Saved message highlight*/
  --sigBackgroundMessageSaved:#111;
 /*Saved message hover*/
  --sigBackgroundMessageSavedHover:#222;
 /*Non-saved message hover*/
  --sigBackgroundMessageHover:#200;
 /*Reaction element background*/
  --sigBackgroundPrimary:#000a;

/*Applies to multiple elements*/
 /*UI element gradient*/
  --sigSurface:transparent;
 /*Background color left panel*/
  --sigMain:#000;
  
/*Text*/
 /*Color of almost all white text*/
  --sigTextPrimary:#eee;
 /*Color of all gray text*
  --sigTextSecondary*/

/*Specific elements.*/
 /*Top chat compose button background*/
  --sigButtonPrimary:#222;
 /*Search button*
  --sigIconPrimary*/
  /*Logo color, devider top left color*
  --sigIconTertiary*/
 /*Chat opened/recieved/sent icon color*
  --sigChat*/
 /*Red snap w/o audio icon color*
  --sigSnapWithoutSound*/
 /*Purple snap w/ audio  icon color*
  --sigSnapWithSound*/
}

.AMei8{/*Applies to the activity bubbles at the bottom of chat.*/
 /*Text color*/
  color:#fff;
 /*Highlight*/
  background-color:transparent;
}

.NvRM8{/*Applies to main chatting window.*/
 /*Chat BG*/
  background:#000;
}
}
