css:
@-moz-document domain('showrss.info') {
    body {
        font-family:"Lato", "Helvetica Neue", Helvetica, Arial, sans-serif;
        font-size: 15px;
        line-height: 1.42857143;
        color: #7a7a7a;
        background-color: #000;
    }
    .sh, .sh:link, .sh:visited, .sh:link strong, .sh:visited strong,
    .sd, .sd:link, .sd:visited, .sd:link strong, .sd:visited strong,
    .fhd, .fhd:link, .fhd:visited, .fhd:link strong, .fhd:visited,
    .hd, .hd:link, .hd:visited, .hd:link strong, .hd:visited,
    .sdre, .sdre:link, .sdre:visited, .sdre:link strong, .sdre:visited strong,
    .fhdre, .fhdre:link, .fhdre:visited, .fhdre:link strong, .fhdre:visited strong,
    .hdre, .hdre:link, .hdre:visited, .hdre:link strong, .hdre:visited strong,
    .glyphicon, .glyphicon-cog,
    .q-anyhd, .q-sd, .q-any {
        color: #b8b8b8;
    }
    .set {
        color: #a70000;
        font-size: 12px;
    }
    .list-group-item {
        position: relative;
        display: block;
        padding: 10px 15px;
        margin-bottom: -1px;
        background-color: #000;
        border: 1px solid #343434;
    }
    legend {
        display: block;
        width: 100%;
        padding: 0;
        margin-bottom: 21px;
        font-size: 22.5px;
        line-height: inherit;
        color: #fff;
        border: 0;
        border-bottom: 1px solid transparent;
    }
}
