	{
		color: #e30000 !important;
	}

	/*
	RobFlinn's awesome stuff of another style, ChattyFix
	If you don't want the full package of modifications above, give a try of his version at http://userstyles.org/styles/44521
	*/
	#container
	{
		color: #ccc !important; background: #0c1118 !important;
	} 

	#chatty_comments_wrap
	{
		border-top: 1px solid #252930 !important;
	}		

	.border-grd
	{
		background: transparent !important;
		height: 5px !important;
		margin: 0 !important;
	}

	div.article-body p
	{
		font-size: 90% !important;
		line-height: 16px !important;
	} 

	div.olauthor_9980 span.oneline_user::after
	{
		font-size: 100% !important;
	}

	span.author a:link,
	span.author a:visited,
	span.author a:active,
	span.author a:hover
	{
		color: #FFBA00 !important;
	} 

	div.oneline a.oneline_user:link,
	div.oneline a.oneline_user:visited,
	div.oneline a.oneline_user:active,
	div.oneline a.oneline_user:hover,
	div.oneline span.oneline_user
	{
		color: #FFBA00 !important;
		font-weight: normal !important;
	} 

	div.postbody a:link,
	div.postbody a:visited,
	div.postbody a:active,
	div.postbody a:hover
	{
		color: #bfa55d !important;
	} 

	div.postbody a:hover
	{
		color: #e5c050 !important;
	}

	div.fullpost
	{
		border: solid 1px #474747 !important;
	}

	div.oneline span.this_user,
	div.oneline span.this_user:link,
	div.oneline span.this_user:visited,
	div.oneline span.this_user:active
	{
		color: #44AEDF !important;
		font-weight: bold !important;
	}

	div.fpmod_informative
	{
		border-top: solid 2px #00bff3 !important;
	} 

	span.user
	{
		color: #44AEDF !important;
	}
	
