css:
@-moz-document url-prefix("https://forum.arizona-rp.com/")
{


/* 
text-decoration: underline; 

border: 1px solid #505662;
border-left: 2px solid #3b596c;

    background: #383d48; -- inside block
	button background: linear-gradient(#7daccb, #5995bc);

*/


:root {
	--cs-bg-0: #292e39;
    --cs-bg-1: #313742;
	--cs-bg-2: #383d48;
	--cs-border: 1px solid #505662;
	--cs-bg-3: #505662;
    --cs-text-dark: #959fb4;
	--cs-text-main: #a9b1c2;
	--cs-text-active: #7daccb;
}
::selection {
	background-color: var(--cs-bg-3);
}

.input {
	background: var(--cs-bg-2);
	border: var(--cs-border);
}
/* header/nav */
.p-nav {
    background: var(--cs-bg-1);
}

.p-header-logo--before {
	background-image: none !important;
}
.p-header-logo {
	background: none !important;
}
.p-nav-list .p-navEl.is-selected {
	color: var(--cs-text-active);
	box-shadow: var(--cs-text-active);
	font-weight: 700;
	border-bottom: 3px solid;
}

.p-nav-list .p-navEl-link, .p-nav-list .p-navEl-splitTrigger {
    padding-top: 25px;
    padding-bottom: 25px;
}
.menu-content {
	background: var(--cs-bg-1);
}
.p-nav-list .p-navEl.is-menuOpen {
	color: var(--cs-text-active)
}


/* main body */

.p-pageWrapper { 
	background: var(--cs-bg-0);
}

.block.block--category .block-header, .block.block--category .block-body, .p-sectionLinks, .p-body-nav, .block-container, .p-body-header {
	background: var(--cs-bg-1);
	border: 1px solid var(--cs-bg-1);
}
.quick-access .button {
	background: var(--cs-bg-3);
}

.node-icon i:before {
	color: var(--cs-text-main);
}
.node--unread .node-icon i:before {
	color: var(--cs-text-active)
}

.button, a.button, .button.button--primary, a.button.button--primary {
	font-weight: bold;
	background: linear-gradient(#7daccb, #5995bc);
}

.p-body-nav .collapseTrigger:not(.button) {
	background: var(--cs-bg-3);
}

.block-body .node:hover {
    transform: scale(1.005);
    background: var(--cs-bg-2)
}
a:hover {
    color: var(--cs-text-main);
    text-decoration: underline;
}

.p-breadcrumbs--container, .node+.node {
	border-bottom: var(--cs-border)
}
/* server section */

.threadSeparator.sticky {
	color: var(--cs-text-dark);
    background: var(--cs-bg-2);
	background-color: none;
    border-top: none;
    border-bottom: none;
    padding: 3px;
    background-image: none;
}

.structItemContainer.sticky {
	background-color: rgb(49 55 66);
	border-left: 3px solid #ff0c0c
}

.structItem--thread.is-locked {
	opacity: 1;
}
.structItem.structItem--thread.is-locked {
	background-color: var(--cs-bg-2);
	background-image: linear-gradient(-45deg, var(--cs-bg-0) 25%, transparent 0, transparent 50%, var(--cs-bg-0) 0, var(--cs-bg-0) 75%, transparent 0);
}

.structItem {
	border-top: var(--cs-border);
}

.threadSeparator {
	color: var(--cs-text-dark);
	padding: 4px;
	background: var(--cs-bg-2);
}

.button.is-disabled, a.button.is-disabled {
	background: var(--cs-bg-1);
}

.block-filterBar {
	background: var(--cs-bg-1);
	border: 1px solid var(--cs-bg-1);
	border-bottom: var(--cs-border);
}

.button.button--link, a.button.button--link {
	background: var(--cs-bg-1);
	border: var(--cs-border);
}

.button.button--link:hover, a.button.button--link:hover, .button.button--link:active, a.button.button--link:active, .button.button--link:focus, a.button.button--link:focus {
	background: var(--cs-bg-2);
}


/* pages */
.pageNav-page>a {
	padding: 7px 10px
}
.pageNav-page {
	background: var(--cs-bg-1);
	border: var(--cs-border);
}

.pageNav-jump {
	background: var(--cs-bg-1);
	border: var(--cs-border);
	padding: 7px 8px;
}

/* Footer */
.p-footer {
	background: var(--cs-bg-1);
}
.xfworld-copyright:after {
    content: "Redesigning by Kevin Hatiko";
    display: block;
    font-size: 14px;
}


/* threads */

.block--messages .message, .block--messages .block-row {
	background: var(--cs-bg-1);
	border: none;
}
.message-cell.message-cell--user, .message-cell.message-cell--action {
	background: var(--cs-bg-1);
	border: none;
}

.message-attribution {
	border-bottom: var(--cs-border);
}

.message-signature {
	border-top: var(--cs-border);
}

.blockStatus {
	background: var(--cs-bg-1);
	border: var(--cs-border);
}

/* thream blocks */
.bbCodeBlock {
	background: var(--cs-bg-2);
	border: var(--cs-border);
	border-left: 2px solid var(--cs-text-active);
}
.bbCodeBlock-title {
	background: var(--cs-bg-1);
}


/* input */

.fr-box.fr-basic {
	background: var(--cs-bg-2);
	border: var(--cs-border);
}

.fr-box.fr-basic.is-focused {
	background: var(--cs-bg-0);
}

/* input-control */


.fr-box.fr-basic.is-focused .fr-toolbar.fr-top {
	background: var(--cs-bg-1);
}
.fr-command.fr-btn+.fr-dropdown-menu {
	background: var(--cs-bg-1);
}
.fr-command.fr-btn+.fr-dropdown-menu .fr-dropdown-wrapper .fr-dropdown-content ul.fr-dropdown-list li a.fr-active, .fr-toolbar .fr-more-toolbar, .fr-toolbar .fr-command.fr-btn.fr-open:not(:hover):not(:focus):not(:active) {
	background: var(--cs-bg-3);
}
.fr-toolbar .fr-command.fr-btn:hover {
	background: var(--cs-bg-1) !important;
}

/* user group */
.username--style74 {
	text-shadow: #3973f6 1px 1px 10px;
    color: #3c76ff;
}

/* user profile */
.menu-row.menu-row--alt {
	background: var(--cs-bg-2);
}
.menu {
	box-shadow: 0 4px 15px 0 rgb(0 0 0 / 20%);
}
.menu-linkRow:hover {
	border-radius: 1px;
	background: var(--cs-bg-2);
}
.menu-footer {
	background: var(--cs-bg-1);
}

.menu-tabHeader {
	background: var(--cs-bg-1);
	border-bottom: var(--cs-border);
}
.menu-tabHeader .tabs-tab.is-active {
	color: var(--cs-text-active);
	border-color: var(--cs-text-dark);
	font-weight: bold;
}

.message-responseRow, .block-tabHeader {
	background: var(--cs-bg-2);
    border: none;
}


}
