css:
/* Create animated gradient background */
body {
  background: linear-gradient(-45deg, #ee7752, #e73c7e, #23a6d5, #23d5ab);
  background-size: 400% 400%;
  animation: gradient 15s ease infinite;
}

/* Define gradient animation */
@keyframes gradient {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}

/* Add a fancy font */
h1, h2, h3, h4, h5, h6 {
  font-family: 'Pacifico', cursive;
}

/* Use a neon effect for text */
h1 {
  font-size: 4rem;
  color: #fff;
  text-shadow: 0 0 10px #fff,
               0 0 20px #fff,
               0 0 40px #0ff,
               0 0 80px #0ff,
               0 0 160px #0ff;
  animation: neon 1.5s ease-in-out infinite alternate;
}

/* Define neon animation */
@keyframes neon {
  from {
    text-shadow: 0 0 10px #fff,
                 0 0 20px #fff,
                 0 0 40px #0ff,
                 0 0 80px #0ff,
                 0 0 160px #0ff;
  }
  to {
    text-shadow: 0 0 20px #fff,
                 0 0 30px #fff,
                 0 0 60px #0ff,
                 0 0 120px #0ff,
                 0 0 200px #0ff;
  }
}

/* Add a glow effect to links */
a {
  color: #fff;
  text-decoration: none;
  text-shadow: 0 0 10px #fff,
               0 0 20px #fff,
               0 0 40px #0ff,
               0 0 80px #0ff,
               0 0 160px #0ff;
  transition: all 0.2s ease-in-out;
}

a:hover {
  color: #0ff;
  text-shadow: 0 0 20px #fff,
               0 0 30px #fff,
               0 0 60px #0ff,
               0 0 120px #0ff,
               0 0 200px #0ff;
}
