css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("shushubuyue.net"),domain("shushubuyue.com")
{
*{-webkit-box-sizing:border-box;-moz-box-sizing:border-box;box-sizing:border-box;-webkit-transition:background .2s,color .1s;-ms-transition:background .2s,color .1s;-o-transition:background .2s,color .1s;-moz-transition:background .2s,color .1s;transition:background .2s,color .1s;}
body{font-size:12px !important;background:#E8E8EE !important;position:static}
a, a.button-link{color:#999 !important}
.page-group,.buttons-tab{width:320px !important;}
.buttons-tab .button{height:1rem;line-height:1rem}
#chat header.top-nav .title, #chat header a, #chat header .button-nav, #chat header .title-en{color:#999 !important}
.title{font-size:8px !important;}
.tab-link{font-size:12px !important;}
#partnerInfoText{font-size:12px !important;}
.bottom-tool-bar{font-size:12px !important;}
.button-link{font-size:12px !important}
.tab-link{font-size:8px !important}
.msg-img{font-size:0.6rem !important}
.message.right .message-text,.message.left .message-text{background-color:#E8E8EE;}
.sys-msg-partner-info .message-text{color:#666;}
#partnerInfoText{color:#999;}
.msg-footer{height:3.5rem;font-size:12px;}
.sys-msg-warn{display:none;}
.msg-voice{height:1rem;display:none;}
.advert{display:none}
.leave-action-instance{font-size:12px;height:2rem;width:2rem;color:#ccc !important;line-height:1.2}
.sys-broadcast #partnerInfoText{color:#ccc !important;}
.readSign{display:hidden;font-size:8px;color:#999 !important;background:#eee;}
.icomoon-2x,.msg-voice,.msg-topic-card a, .msg-dice img{font-size:0.6rem !important;}
.msg-type-img{max-width:50px;max-height:50px;}
.actions-modal{width:320px;}
.actions-modal-button{font-size:12px;line-height:3;}
.actions-modal-button.bg-danger, .actions-modal-label.bg-danger{background:#666}
.viewer-container{max-width:320px;}
.modal-overlay, .popup-overlay, .preloader-indicator-overlay{max-width:320px;}
.title-en{display:none;}
.viewer-container img{margin-left:80px !important;width:160px !important;height:auto !important}
.van-overlay{max-width:320px;}
.van-overlay div{font-size:12px !important;}}
