css:
@-moz-document domain('web.snapchat.com'){
body{
 /*Rounding on corners*/
  --sigElevationCellRadius:0;
 /*How to render text*/
  text-rendering:optimizeLegibility;
}

.NvRM8{/*Applies to main chatting window.*/
 /*No boarder, slightly bigger UI*/
  border:0;
 /*Padding left of whole chat*/
  padding-inline-start:0;
 /*Padding right of chat black*/
  padding-inline-end:0;
}
._XMCw{
 /*Padding right of chat gray*/
  padding-inline-end:0;
}
.Vbjsg{/*Gray chatbox container*/
 /*Left padding*/
  padding-inline-start:0;
 /*Bottom padding*/
  padding-block-end:0;
}
}
