css:
* {
    box-sizing: border-box;
}
html {
    background: gray;
}
body {
    color: #545b64;
    line-height: 20px;
    font-size: 14px;
    font-family:'Amazon Ember';
    padding: 20px 30px;
    margin: 0;
    width: 300px;
    background: #FFF;
    box-shadow: 0 0 5px #000;
    float: right;
}
h2:first-child, h3:first-child, h4:first-child, p:first-child, ul:first-child, ol:first-child {
    margin-top: 0;
}
h2:last-child, h3:last-child, h4:last-child, p:last-child, ul:last-child, ol:last-child {
    margin-bottom: 0;
}
h1, h2, h3, h4 {
    margin-top: 25px;
    color: #16191f;
    margin-bottom: 10px;
}
h1 {
    font-size: 18px;
    margin-top: 0;
    font-weight: bold;
    border-bottom: 1px solid #CCC;
    margin: 0 -30px 20px -30px;
    padding: 0 30px 20px 30px;
}
h2 {
    font-size: 18px;
    font-weight: 400;
}
h3 {
    font-weight: 400;
    font-size: 16px;
}
h4 {
    margin-bottom: 5px;
    font-weight: 700;
    font-size: 15px;
}
p, ol, ul, li {
    margin: 10px 0;
    padding: 0;
}
li:last-child {
    margin-bottom: 0;
}
ol, ul {
    padding-left: 20px;
}
a {
    color: #0073bb;
    text-decoration: none;
}
pre, code {
    background: #f2f3f3;
    font-size: 12px;
    line-height: 15px;
    font-family: Monaco, Menlo, Consolas, Courier Prime, Courier, Courier New, monospace;
    padding: 0 5px;
}
hr {
    border: 0;
    height: 1px;
    background: #eaeded;
    margin: 10px -10px;
}
