css:
@-moz-document domain('foro.unionfansub.com'), domain('torrent.unionfansub.com') {
  /* Variables */
  :root {
      --color-text: #e9e9e9;
      --color-text-alt: #dcdcdc;
      --color-accent: #a8a8a8;
      --color-accent-dark: #7e7e7e;
      --color-accent-light: #c2c2c2;
      --color-border: #525252;
      --shade-0: #101010;
      --shade-50: #161616;
      --shade-100: #1f1f1f;
      --shade-200: #2f2f2f;
      --shade-300: #3f3f3f;
      --shade-400: #525252;
      --shade-500: #656565;
      --shade-600: #7e7e7e;
      --shade-700: #979797;
      --shade-800: #b0b0b0;
      --shade-900: #dcdcdc;
    }

    /* Texto */
    a {
      color: var(--color-accent) !important;
    }
    a:hover {
      color: var(--color-accent-light);
    }
    h1,
    h2,
    h3,
    h4,
    h5,
    h6,
    s,
    div,
    .navigation {
      color: var(--color-text);
    }
    p {
      color: var(--color-text-alt);
    }
    strong[title="VIP"] {
      color: greenyellow !important;
    }
    div[style*="#FFD"], div[style*="#BBCCBB"] {
      color: var(--shade-0);
      padding: 2px 5px ;
    }

    /* Inputs */
    input[type="text"],
    input[type="email"],
    input[type="password"],
    textarea {
      background-color: var(--shade-300);
      border: 1px solid var(--color-border);
      color: var(--color-text);
    }
    input[type="text"]:focus,
    input[type="email"]:focus,
    input[type="password"]:focus,
    textarea:focus {
      border-color: var(--shade-600);
      background-color: var(--shade-200);
    }
    input[type="checkbox"],
    input[type="radio"] {
      background-color: var(--shade-600);
      border: 1px solid var(--color-border);
      color: var(--color-text);
    }
    input[type="checkbox"]:focus,
    input[type="radio"]:focus {
      border-color: var(--color-accent);
    }
    input[type="checkbox"]:checked::before,
    input[type="radio"]:checked::before {
      background-color: var(--color-accent);
    }
    input[type="range"]::-webkit-slider-runnable-track {
      background-color: var(--shade-600);
      border: none;
    }
    input[type="range"]::-webkit-slider-thumb {
      background-color: var(--color-accent);
    }
    input[type="range"]:focus {
      outline-color: var(--color-accent);
    }
    ::-webkit-scrollbar-thumb {
      background-color: var(--color-accent);
    }
    ::-webkit-scrollbar {
      width: 10px;
      background-color: var(--shade-600);
    }
    ::-webkit-scrollbar-track {
      background-color: var(--shade-600);
    }

    /* Foto de cabecera */
    #top, #logostrip {
      filter: saturate(0.5);
    }

    /* Fondo y texto para la mayor√≠a de elementos (Muy oscuro) */
    html,
    body,
    section,
    th,
    #panel,
    #container,
    #welcome,
    .top,
    .tcat td,
    .menu,
    .main,
    .mainouter,
    .masinfo,
    .masinfo .ficha,
    .ficha,
    .tfoot,
    .tfoot td,
    .bottommenu {
      background-color: var(--shade-0);
      background-image: linear-gradient(var(--shade-100), var(--shade-50));
      color: var(--color-text);
    }

    /* Fondo para citas (Oscuro) */
    cite,
    blockquote {
      background-color: var(--shade-0);
      background-image: linear-gradient(var(--shade-200), var(--shade-100));
      color: var(--color-text);
    }

    /* Fondo para las notas, avisos, etc */
    div.notas,
    div.info,
    div.notas *,
    div.info *{
      color: var(--shade-0);
    }

    /* Fondo para spoiler (Menos oscuro) */
    .forumdisplay_sticky td,
    .spoil {
      background-color: var(--shade-0);
      background-image: linear-gradient(var(--shade-300), var(--shade-0));
      color: white;
    }

    /* Fondo para filtro (Claro) */
    .filter.spoil {
      background-color: var(--shade-0);
      background-image: linear-gradient(var(--shade-900), var(--shade-600));
    }

    /* Colores para las tablas */
    table {
      background-color: var(--shade-600);
      border-collapse: collapse;
    }
    table th,
    table td {
      color: var(--color-text);
    }
    table th {
      background-color: var(--shade-500);
    }
    table td {
      background-color: var(--shade-600);
    }
    table td,
    .trow1 td {
      background-color: var(--shade-0);
      background-image: linear-gradient(var(--shade-300), var(--shade-200));
    }
    .trow2 td {
      background-color: var(--shade-0);
      background-image: linear-gradient(var(--shade-100), var(--shade-200));
    }
    .trow_sep td,
    td.content {
      background-color: var(--shade-0);
      background-image: linear-gradient(var(--shade-50), var(--shade-100));
    }
    .forumdisplay_regular.trow1:hover td,
    .forumdisplay_regular.trow2:hover td {
      background-color: var(--shade-0);
      background-image: linear-gradient(var(--shade-600), var(--shade-400));
    }

    /* Buttons */
    button {
      background-color: var(--shade-500);
      border: 1px solid var(--color-border);
      color: var(--color-text);
    }
    button:hover {
      background-color: var(--shade-600);
    }
    button:focus {
      border-color: var(--color-accent);
    }
    select {
      background-color: var(--shade-600);
      border: 1px solid var(--color-border);
      color: var(--color-text);
    }
    select:focus {
      border-color: var(--color-accent);
    }
    select option {
      background-color: var(--shade-500);
      color: var(--color-text);
    }
    .btn {
      background-color: var(--shade-500);
      border: 1px solid var(--color-border);
      color: var(--color-text);
      display: inline-block;
      text-decoration: none;
      text-transform: uppercase;
    }
    .btn:hover {
      background-color: var(--shade-600);
    }
    .btn:focus {
      border-color: var(--color-accent);
    }
}
