css:
@-moz-document domain("roblox.com")
{
  .ad,.ad *,.img_ad,.ad-annotations,#google_image_div,#aw0,#AdvertisingLeaderboard
  {
    display: none !important;
  }

  .ad,.ad *,.img_ad,.ad-annotations,#google_image_div,#aw0,#AdvertisingLeaderboard
  {
    display: none !important;
  }

  #header
  {
    background-color: #000000;
  }

  .navigation .notification-icon,
.rbx-highlight
  {
    background: #000000;
  }

  #chat-header
  {
    background: #000000;
    ;
  }

  .slide-item-container-left,
.profile-avatar-right
  {
    background: #000000;
  }

  .rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading,
.rbx-tabs-horizontal .rbx-tab-heading:hover,
.rbx-tabs-horizontal .rbx-tab-heading:focus,
.rbx-tabs-horizontal .rbx-tab-heading:active
  {
    box-shadow: 0 -4px 0 .6 #000000 inset;
    ;
  }

  .forum-table-header,
body .forum-table-header th,
body .forum-table-footer,
#ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th,
#ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th
  {
    background: #000000 !important;
 /*Forum colors and messaging underline color*/
    ;
  }

  .rbx-upgrade-now #upgrade-now-button,
.rbx-btn-secondary-xs.btn-more,
#AjaxCommentsContainer > div > div.comment-form > form > button,
.rbx-vip-server-create,
.btn-secondary-xs
  {
    background-color: #000000 !important;
    border-color: #000000;
    ;
  }

  .rbx-upgrade-now #upgrade-now-button:hover,
.rbx-btn-secondary-xs.btn-more:hover,
#AjaxCommentsContainer > div > div.comment-form > form > button:hover,
.rbx-vip-server-create:hover,
.btn-secondary-xs:hover
  {
    background: #000000 !important;
    border-color: #000000;
  }

  #TopAbpContainer,
.ad-annotations,
#LeftGutterAdContainer,
#RightGutterAdContainer,
#Skyscraper-Adp-Right,
#Skyscraper-Adp-Left,
.abp-spacer,
#GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even,
#GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd,
#GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even,
#GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd,
.sponsored-game,
.alert-info,
#mCSB_1_container > ul > li.font-bold.small,
#mCSB_1_container > ul > li.rbx-nav-sponsor
  {
    display: none !important;
  }

  #navbar-search-input,
#navbar-search-btn
  {
    border: 1px solid #000000;
 /*Search border color*/
    ;
  }

  body
  {
    background: transparent !important;
  }

  .content
  {
    background: transparent !important;
  }

  #fb-root
  {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url("https://im.ezgif.com/tmp/ezgif-1-1ce7691fc8.gif") !important;
 /* Background */
    image-rendering: pixelated;
    background-size: cover !important;
    background-attachment: scroll !important;
  }

  #party_none_title,
div.friend_dock_chatbox_titlebar.chat-header-blink-off
  {
    background: linear-gradient(to bottom, #000000, #000000);
  }

  #ctl00_cphRoblox_RightColumn,
#Body > table > tbody > tr > td:nth-child(3),
#Body > table > tbody > tr > td:nth-child(4)
  {
    display: none;
  }

  #GamesPageLeftColumn
  {
    width: auto;
    margin-right: 10px !important;
    margin-left: 10px !important;
  }

  #GamesListsContainer > div
  {
    background-color: transparent !important;
    width: 100% !important;
  }

  #navigation
  {
    background-color: rgba( 0, 0, 0, 1) /*Left side navi*/;
  }

  .rbx-btn-primary-lg
  {
    background-color: rgba(255, 255, 255, 0.5);
  }

  .rbx-btn-primary-lg:hover, .rbx-btn-primary-lg:focus
  {
    background-color: rgba(255, 255, 255, 0.5);
  }

  *
  {
    font-family: Segoe UI !important;
  }

  *
  {
    color: White;
  }

  *
  {
    color: white;
    text-shadow: 2px 2px 0 #000,
     -1px -1px 0 #000,  
      1px -1px 0 #000,
      -1px 1px 0 #000,
       1px 1px 0 #000;
  }

  img.game-card-thumb
  {
    border: 4px solid #555;
  }

  .thumbnail-2d-container.game-card-thumb
  {
    border: 4px solid #555;
  }

  .carousel-thumb
  {
    border-radius: 11px;
  }

  .game-main-content .game-play-buttons .btn-common-play-game-lg
  {
    -webkit-transition: background-color 0.5s ease;
    -moz-transition: background-color 0.5s ease;
    -o-transition: background-color 0.5s ease;
    transition: background-color 0.5s ease;
  }

  .game-main-content .game-play-buttons .btn-common-play-game-lg
  {
    -webkit-transition: border-color 0.2s ease;
    -moz-transition: border-color 0.2s ease;
    -o-transition: border-color 0.2s ease;
    transition: border-color 0.2s ease;
  }

  .game-main-content .game-play-buttons .btn-common-play-game-lg:hover
  {
    border-color: black;
    background-color: black;
  }

  .nav.nav-tabs
  {
    transition: padding 0.5s ease;
  }

  .nav.nav-tabs:hover
  {
    padding: 6px;
  }

  .icon-robux-28x28.roblox-popover-close
  {
    transition: transform 0.5s ease;
  }

  .icon-robux-28x28.roblox-popover-close:hover
  {
    transform: rotate(-15deg);
  }

  .nav-settings-icon.rbx-menu-item
  {
    transition: transform 0.2s ease;
  }

  .nav-settings-icon.rbx-menu-item:hover
  {
    transform: rotate(-360deg);
  }

  .rbx-visit-button-access-denied span
  {
    display: none;
  }

  .rbx-visit-button-access-denied:after
  {
    content: "You cant access this place loser";
  }

  .chat-main .chat-header .notification-red
  {
    background-color: Grey;
    text-shadow: 0px 0px;
  }

  .cursor-pointer
  {
    opacity: 0.5;
    transition: opacity 0.5s ease;
  }

  .cursor-pointer:hover
  {
    opacity: 1;
  }

  .interaction-item .player-action
  {
    text-shadow: 0px 0px;
  }

  .profile-join-game
  {
    text-shadow: 0px 0px;
  }

  .btn-full-width.place-btn
  {
    text-shadow: 0px 0px;
  }

  .enable-three-dee.btn-control.btn-control-lg
  {
    text-shadow: 0px 0px;
  }

  .notification-red.notification
  {
    text-shadow: 0px 0px;
  }

  .btn-primary-md
  {
    text-shadow: 0px 0px;
  }

  .tab-item
  {
    text-shadow: 0px 0px;
  }

  .widget-name
  {
    text-shadow: 0px 0px;
  }

  .studio-launch, .title, .place-inactive, .item-universe, .header-text
  {
    text-shadow: 0px 0px;
  }

  .checkbox-label
  {
    text-shadow: 0px 0px;
  }

  .btn-control.btn-control-large
  {
    text-shadow: 0px 0px;
  }

  .totals-label
  {
    text-shadow: 0px 0px;
  }

  .game-card-container:hover .game-card-name.game-name-title
  {
    transition: color 0.5s ease;
  }

  .game-card-container:hover .game-card-name.game-name-title
  {
    color: Red;
  }

  .btn-economy-robux-white-lg.PurchaseButton
  {
    transition: background-color 0.4s ease;
    transition: border-color 0.2s ease;
  }

  .btn-economy-robux-white-lg.PurchaseButton:hover
  {
    background-color: #a30000;
    border-color: #a30000;
  }
}
