css:
@-moz-document domain("filelist.io") {  

   html {
      background: url('https://i.postimg.cc/CL8tX2qZ/Star-Wars.jpg') no-repeat 
      center center fixed;
      -webkit-background-size: cover;
      -moz-background-size: cover;
      -o-background-size: cover;
      background-size: cover;
  }
  body {
      background: none;
  }
  .wrapper {
      background: none;
  }
  .mainheader {
      background: none;
  }
  .nblock-content a {
      color: #39cd2a;
  }
  #navigation {
       background: #2f445c;
       border: 3px solid #000000;
       box-shadow: none;
       padding: 10px 10px 10px 10px;
       top: 170px;
       width: 947px;
       height: 44px;
  }
  #navigation li a {
       color: #9ACD32!important;
       text-shadow: 1px 1px 1px #111;
  }
  #navigation li a:hover {
       color: #f06816!important;
       text-shadow: 1px 1px 1px #111;
  }
  #nav {
       height: auto;
       padding: 6px 20px;
       font-size: 13px;
       font-weight: bold;
       justify-content: center;
       align-items: center;
       display: flex;
  }
  .torrenttable {
       float: left;
       padding: 2px 0px;
       border: none;
       border-bottom: solid 1px #381010;
  }
  .torrenttable img {
       border-radius: 9px;
  }
  .torrenttable a {
       color: #9ACD32!important;
       text-shadow: 1px 1px 1px #111;
  }
  .cblock-innercontent table form {
       padding: 10px -5px 10px 10px!important;
  }
   select {
        background: #381010;
        border: 1px solid #9ACD32;
        padding: 10px 1px 10px 10px;
        color: #fff;
  }
  .noborder input:hover,select:hover {
        background: #222;
        border: 1px solid #5f35b2;
        padding: 10px 1px 10px 10px;
        color: #fff;
  }
   .statusbar {
         border: 3px solid #000000;
         background: #2f445c;
         text-align: center;
         width: 425px;
  }
   .statusbar a {
        color: #9ACD32;
  }
   #container a {
        color: #9ACD32;
  }
   .cblock-top, .cblock-bottom {
        border-top: 0px solid #000000;
        border-bottom: 1px solid #000000;
  }
   .cblock-content {
         border-left: 5px solid #000000;
         border-right: 5px solid #000000;
  }
   .cblock-innercontent {
         background: #334e30;
         width: 960px;
  }
   .nblock-content {
         width: 678px;
         background: #2f445c;
         border: 3px solid #000000;
  }
   .sblock-content {
         width: 274px;
         background: #2f445c;
         border: 3px solid #000000;
  }
   .nblock-headerleft, .nblock-header, .nblock-headerright {
         background: #381010;    
  }
   .sblock-headerleft, .sblock-header, .sblock-headerright {
         background: #381010;    
  }
   .cblock-headerleft, .cblock-header, .cblock-headerright {
         background: #381010;   
  }
   .colhead {
          background: rgba(44, 23, 79, 0.9);    
  }
    option {
          color: white!important;
  }
    input.btn {
          padding: 5px 10px 15px 5px!important;
          color: #000000!important;
          background: #C0C0C0;
  }
   .logo {
          position: relative;
          background: transparent url('https://i.postimg.cc/JhpdjV9F/Tema-Filelist-Star-Wars.png') no-repeat;
          margin-top: 10px;
          max-height: 130px;
          min-width: 300px;
          padding-bottom: 20px;
          position: relative;
          top: 0px;
          left: 80px;
  }
    ::-webkit-scrollbar {
           width: 1.4em;
  }
    ::-webkit-scrollbar-track {
           background: #C0C0C0;
  }
    ::-webkit-scrollbar-thumb {
           background: #2f445c;
  }
    #poll_separator {
           margin-top: 5px;
           padding-top: 5px;
           border-top: 1px solid rgba(237, 248, 255, 0.01);
  }
    .mainheader {
           width: 100%;
           height: 245px;
           min-width: 1000px;
  }
    .status_avatar {
          margin: 6px 40px 6px 2px;
          border: 1px solid;
          border-color: #5f35b;
          width:55px;
          height:65px;
  }
    body {
          letter-spacing: 0.2px;
          line-height: 13px;
  }
    .cblock-content {
          width: 960px;
          padding: 7px;
          background: #2f445c;
          position: relative;
          border-radius: 10px;
  }
     select option {
          color: #fff !important;
  }
    .colhead {
          font-weight: bold;
          color: #E0E0E0;
          background: #334e30;
          border: 1px solid #381010;
          padding: 5px;
  }
    div#footer {
          background: #2f445c;
  }
   div#copyright{
          background-color: #000000
  }
    #copyrightInner {
        width: 970px;
        margin: 0 auto;
        font-size: 11px;
        color: #db0f0;
        padding: 13px 0;
        position: relative;
 }
 .main {
        width: 912px;
        height: 170px;
        margin: 0 auto;
        color: #FFFFFF;
        background: #1f1f1f;
        border-radius: 5px;
 }
 hr.separator {
       border: 0;
       height: 3px;
       width: 600px;
       margin: 11px auto 0px auto;
       background-image: -webkit-linear-gradient(left, rgba(0,0,0,0), rgba(0,0,0,0.75), rgba(0,0,0,0));
 }
 #poll_separator {
        margin-top: 5px;
        padding-top: 5px;
        border-top: 1px solid #000000;
 }
  .visitedlinks a:visited {
        text-decoration: none;
        color: #f06816 !important;
 }
  .staffbox {
        width: 100px;
        border: 1px solid #cacaca;
        padding: 5px;
        margin: 0px 4px 10px 6px;
        background: #0b0e10;
        text-align: center;
  }
  .small {
        font-size: 7.5pt;
  }
  .pager a, .pager a:link {
        margin: 0 2px;
        padding: 4px 6px;
        background: #4C4646;
        border: 1px solid #E2E2E2;
        text-decoration: none;
        font-weight: normal;
        font-size: 8pt;
        color: #666;
        border-radius: 3px;
  }
   input {
        background: #C0C0C0;
        padding: 5px 4px;
        border: 1px solid #e2e2e2;
        border-radius: 3px;
   }
   input.sbtn {
         background: #C0C0C0;
         border: 1px solid #9ACD32;
         color: #000000! important;
   }
   textarea {
         font-size: 8pt;
         background: #C0C0C0;
         border: 1px solid #9ACD32;
         max-width: 650px !important;
         max-height: 300px !important;
         border-radius: 3px;
   }
   .cblock-header {
         width: 968px;
         height: 30px;
         float: left;
   }
   .noborder input {
         background: #C0C0C0;
         border: 1px solid #9ACD32;
         padding: 10px 1px 10px 10px;
         color: #000000;
   }
   .status_avatar {
         margin: 6px 45px 6px 7px;
         border: 1px solid;
         border-color: #5f35b;
         width: 55px;
         height: 65px;
   }
   input.lbtn {   
         background: #C0C0C0; 
         width: 140px;
         height: 20px;
         padding: 2px 0px 4px 0px;
         font-size: 7pt;
         border: none;
         color: #555!important;
   }
   .nblock-header h4 {
         padding-left: 4px;
         line-height: 30px;
         font-size: 11pt;
         color: #9ACD32;
         float: left;
   }
   .sblock-header h4 {
          padding-left: 4px;
          line-height: 30px;
          font-weight: bold;
          font-size: 11pt;
          color: #9ACD32;
          float: left;
   }
   .cblock-header h4 {
          padding-left: 4px;
          line-height: 30px;
          font-weight: bold;
          font-size: 11pt;
          float: left;
          color: #9ACD32;
   }
   h1 {
          font-size: 12pt;
          color: #9ACD32;
   }
   table.main, table.mainouter {
          background-color: #282828;
          height: 1px;
   }
  input.bigbtn {
          background-repeat: no-repeat;
          background: #C0C0C0;
          background-position: 0 0;
          width: 160px;
          height: 25px;
          padding: 2px 0px 4px 0px;
          font-size: 8pt;
          border: none;
          cursor: pointer;
          color: #000000!important;
  }
  td {
          font-size: 8pt;
          border: 1px solid #381010;
          color: #E0E0E0;
  }
  #forummain .forumrow {
          border: 1px solid #381010;
          margin: -1px 0px;
          padding: 4px;
  }
  #viewforum .viewforumrow {
          border: 1px solid #381010;
          margin: -1px 0px;
          padding: 4px;
  }
  .normalline {
    border: 1px solid #381010;
  }
}
