css:
@-moz-document url(https://developer.microsoft.com/en-us/graph/graph-explorer) {
	:root{
		--properHeight: 100vh;
		--properHeightRow107: calc(var(--properHeight) - 67px);
		--properHeightLeftList: calc(var(--properHeight) - 200px);
		--msgBarHeight: calc(100vh - 10px);
		--topBarShow: none;
		--topBarHeight: 10px;
		--mediaHeaderWidthCalc: calc(100vw - 38px);
		--expandingBgCol: /*[[headerOverlayColour]]*/;
	}
	body {
		height: 100vh !important;
		background: black !important;
	}
	#uhf-g-nav, .c-uhfh-actions {
		background: #fffffff5 !important;
		border-radius: 5px;
  }
  #uhf-g-nav {
		flex: 0 1 auto !important;
		max-width: fit-content;
  }
  .c-uhfh-actions {
		margin-left: auto !important;
  }
  div#headerUniversalHeader > header > div,
  div#headerUniversalHeader > header {
		background: #fff0 !important;
  }
	#mainContent {
		top: 0px !important; 
  	position: absolute !important;
		width: 100vw;
	}
  #UHFHeader {
  	position: relative !important;
		top: 0px;
		height: var(--topBarHeight);
		width: 100vw;
		z-index: 9999 !important;
		background: var(--expandingBgCol) !important;
  }
  #headerArea {
		display: var(--topBarShow);
		width: 100%;
  }
  #UHFHeader:hover, #UHFHeader:active {
		--topBarShow: block;
		--topBarHeight: fit-content;
		--expandingBgCol: #fff0 !important;
  }
	.app-106 {
  	height: var(--properHeight) !important;
		padding: 0px 15px !important;
  }
	.appRow-107 {
  	height: var(--properHeightRow107) !important;
  }
	.ms-DetailsList {
		max-height: var(--properHeightLeftList) !important;
	}
  @media screen and (min-width: 860px) {
	  div#headerUniversalHeader > header > div {
		 	padding: 0px 10px !important;
    }
		.c-uhfh > [class*="theme-"] {
			background: #fff0;
		}
	  div#headerUniversalHeader > header > div > div.c-uhfh-gcontainer-st {
		 	margin: 0px 10px !important;
			max-width: var(--mediaHeaderWidthCalc) !important;
    }
  }
}
@-moz-document url(https://developer.microsoft.com/en-us/graph/graph-explorer) {
  #socialMediaContainer, #footerArea, #UHFFooter, 
	.ms-MessageBar-content, .content-252, .ms-MessageBar, .ms-MessageBar-multiline, .root-251, 
	div.c-show-pipe.x-hidden-vp-mobile-st, div.f-mobile-title, #uhfLogo, div.wf-menu {
		display: none !important;
		height: 0%;
	}
	
	/* unsure */
	#headerWrapper{
		resize: vertical !important;
		overflow: hidden !important;
	}
}
