css:
@-mod-document domain('youtube.com') {
 
  ytd-watch-flexy:not([theater]):not([fullscreen]):not([no-top-margin]):not([reduced-top-margin]) #primary.ytd-watch-flexy, ytd-watch-flexy:not([theater]):not([fullscreen]):not([no-top-margin]):not([reduced-top-margin]) #secondary.ytd-watch-flexy {
    padding-top: 0px;
}

#columns.ytd-watch-flexy {
    padding-right: 10%;
}

#contentContainer.tp-yt-app-drawer[swipe-open].tp-yt-app-drawer::after {
    visibility: hidden;
}

ytd-watch-flexy[flexy][is-two-columns_] #columns.ytd-watch-flexy {
    justify-content: left;
}

ytd-watch-flexy[flexy] #primary.ytd-watch-flexy {
    margin-left: 0px;
}

#primary.ytd-watch-flexy {
    padding-right: 0px;
}

ytd-watch-flexy[flexy] #secondary.ytd-watch-flexy {
    padding-right: 0px;
}




}
