css:
@-moz-document url-prefix("https://start.me/") {
/*
Rounding of corners
*/

.widget {
    margin-bottom: 15px;
}
.widget__container {
    border-radius: 16px;
}
.pages-bar__item, .pages-bar__list_darkened .pages-bar__item {
    border-radius: 16px;
    line-height: 30px;
}
.widget-header {
    height: 35px;
}
/*
 License removal
id=special-container*/
.special, .widget-page__banner, .special_own {
    display: none;
}

/* Remove searchBar */
.widget-page__searchbar, .widget-page__searchbar_allowed {
    display: none;
}
/* Remove upgrade and share buttons */
.header-right__button_type_custom {
    display: none;
}

/* Remove footer*/
.page-footer {
    display: none;
}

/* Remove promo stuff */
.pages-sidebar__status, .pages-sidebar__status_link, .pages-sidebar__promo, .promo-banner {
    display: none;
}
}
