css:
@-moz-document domain(kleinanzeigen.de) {
	/* MAIN PAGE */
#site-header,
#site-header-top,
.site-base{
  background-color: #404040;
  color: #efefef;
}
#site-logo-claim {
  color: #efefef;
}
#site-mainnav > ul > li.on,
#site-subnav {
  background-color: #404040;  
}
#site-subnav {
  border-color: #303030;
}
#site-mainnav > ul > li.on a,
.breadcrump-summary,
.srchresult-sorting {
  color: #efefef;
}
.mainnav-item-inner {
  background-image: url(https://static.kleinanzeigen.de/static/img/common/navigation-bar/icn-my.svg)
}

/* footer colors */
#site-footer a,.flexlist--header {
  color: #efefef !important;
}

a {
  color: limegreen;
}
svg {
  -webkit-filter: invert(100%); /* safari 6.0 - 9.0 */
          filter: invert(100%);
}
.icon {
  filter: invert(1) !important;
}
.userbadges-vip .icon {
  filter: invert(0) !important;
}

/* hide ads :3 */
.liberty-cls-improved, [data-liberty-position-name=srpb-top-banner], [data-liberty-position-name=zsrp-top-banner], [data-liberty-position-name=soi-top-banner], [data-liberty-position-name^=vip][data-liberty-position-name$=gallery], [data-liberty-position-name=home-billboard], [data-liberty-position-name^=my][data-liberty-position-name$=top-banner], [data-liberty-position-name^=vip][data-liberty-position-name$=billboard] {
  display: none!important;
}

.contentbox {
  background-color: #737373;
}

/*buttons*/
[data-theme=ka] .button-secondary, [data-theme=ka] .iconbox, [data-theme=ka] .button-tertiary,
.Button-secondary{
  background-color: #efefef !important;
}

[data-theme=ka] .button-secondary:hover, [data-theme=ka] .iconbox:hover, [data-theme=ka] .button-tertiary:hover {
  background-color: white;
}

/* notifications, additionals on details page */
.NotificationListItem, .aditem {
  background-color: #999 !important;
}
.NotificationItem--Imagebox {
  background-color: white;
}
.NotificationItem--Main-Top-Right,
.aditem-main--top {
  color: black !important;
}
.NotificationListItem h2 a {}

/*	DETAILED PAGE */
.boxedarticle, .splitlinebox  {
  background-color: #404040;  
}
.boxedarticle h1, 
[class^="boxedarticle"],
[class^="addetailslist"] 
{
  color: #efefef;
}

}
