css:
@-moz-document domain("n-gate.com") {
:root {
    --font-override: "Helvetica Neue", Helvetica, Arial, sans-serif;
    --color-bg: #333;
    --color-fg: #ccc;
    --color-muted: #999;
    --color-link: #eee;
}

body {
    font-family: var(--font-override);
    background-color: var(--color-bg);
    color: var(--color-fg);
}

#main-copy > p > br {
 margin-bottom: 0em;   
}

span.storylink {
    font-variant: none;
    font-weight: bold;
    margin-top: 2em;
    margin-bottom: 0em;
    display: block;
}

#side-bar {
    font-variant: none;
    opacity: 0.25;
}

#main-copy > p {
    font-weight: lighter;
    width: 50%;
    font-size: 1.5em;
}

blockquote {
    border-left: 1px solid var(--color-fg);
    font-size: 98%;
    font-weight: lighter;
    width: 50%;
}

.smalldate {
    color: var(--color-muted)
}
a {
    color: var(--color-link);
    text-decoration: none;
}
}
