css:
@document domain("chat.openai.com") {

@media (min-width: 1280px) {
.xl\:max-w-3xl {
    max-width: 100%;
}}

@media (min-width: 768px)
{
.md\:py-6 {
    padding-bottom: 0rem;
    padding-top: 0rem;
}
}

.prose p {
    margin-bottom: 0em;
    margin-top: 0em;
}

.prose {
    margin-bottom: 0em;
}

@media (min-width:768px){
  .container{max-width:5000px}
}


@media (max-width:370px){
    .hidden{
        display:none
    }
}

@media (min-width:768px){
  .md{
    visibility:visible;
      display: block !important;
  }
}

/*======================================= */
.flex.py-3.px-3.items-center.gap-3.transition-colors.duration-200.text-white.cursor-pointer.text-sm.rounded-md {
  margin-right: 40%;
}

::-webkit-scrollbar {
  width: 2rem;
  background-color: #ccc;
}

::-webkit-scrollbar-thumb {
  background-color: #999;
}

::-webkit-scrollbar-thumb:hover {
  background-color: #666;
}

main > div.flex-1.overflow-hidden > div > div {
    height: calc(100% - 5rem);
}

@media (min-width: 1024px) {
main > div.flex-1.overflow-hidden > div > div > div > div > div {
    max-width: 100%;
} }}
