css:
@-moz-document domain(voxfm.pl),domain(player.voxfm.pl){
:root{
    --bg_1:#212121;
    --bg_2:#262626;
    --bg_3:#444;
    --bg_4:#888;
    --bg_hover:#ffffff17;
    --border_c:#444;
    --color_0:#000;
    --color_1:#dddee1;
    --color_2:#fff;
    --color_3:#333;

  --color-white:var(--bg_1);
  --color-black:var(--color_1);
  --color-font:var(--color_1);
}
body,.header-bar,.gl_wrapper,#hook_content .slot__small .slot__content::after,
.listing-container.v30 .element__headline,.listing-container.v31 a,
.v32.listing-container .element:nth-child(n+3)  .element__headline a,
.section__v1 .slot.slot__small .slot__content::after,.vjsPlayingHistory,
.section__v2 .slot.slot__small .slot__content::after,#before-button, #after-button,
.section__v2 .slot.slot__small.slot_4 .slot__content::after,
.section__v2 .slot.slot__small.slot_5 .slot__content::after,
.two_columns_article__container .related_article__title
{background-color:var(--bg_1)!important;}
.header .bottom-tap-menu .bottom-tap-menu-right
{background:var(--bg_3)!important;}

.artist-hits .single-hit:hover
{background:#ffffff08;}

footer  *:not(.serwis-list),.two_columns_article__container .related_article__title>p,
a, .header  .bottom-tap-bar-item__text span,.listing.v18_side a,
.section__v1 .slot__small p, .section__v2 .slot__small p,
.vjsPlayingHistory__hit__title,
.vjsPlayingHistory
{color:var(--color_1)!important;}
footer .serwis-list a,footer .show-list, footer .copyright__text
{color:var(--color_0)!important;}
/* FIX */
.player__program-image_container::before,.player__program-image_container::after
{background-color:#2121211a!important;}
    /* fooper img */
.listing-container.v31 img{border-radius:20px;}
footer .gl_wrapper,footer .row{padding:0!important;}

/* player */
.player__wide-container{--color-white: #FFFFFF;}
footer li .h5,.player__stream-contact_container a
{color: var(--color-voxfm-main) !important;}
body{overflow: auto!important;}
.fb_iframe_widget iframe{
bottom: 12px!important;
    left: auto;
    top: auto!important;
    width: 400px!important;
}

/* anty adBock detekt */
#adblock-detector-wrapper{display:none;}
div[class*="player-voxfm-pl_topboard"]{aspect-ratio:0!important;}
}
