    color: #122dc8;
}
.waiting-for-leader--warm-up--entry {
    display: flex;
    width: 280px;
    background: #fff;
    align-items: center;
    justify-content: center;
    padding: 10px 0;
    border-radius: 5px;
    margin: 0 auto;
    background: #fff;
    background: linear-gradient(180deg, #c22222 0%, white 50%, #aaaaaa 100%);
    box-shadow: inset 0 0 15px white, 0 1px 8px rgba(0, 0, 0, 0.2);
    line-height: 2em;
    background: green;
}
