css:
@-moz-document domain(eska.pl),domain(player.eska.pl){
:root{
    --bg_1:#212121;
    --bg_2:#262626;
    --bg_3:#444;
    --bg_4:#888;
    --bg_hover:#ffffff17;
    --border_c:#444;
    --color_0:#000;
    --color_1:#dddee1;
    --color_2:#fff;
    --color_3:#333;
}
.gl_wrapper,.listing-container.v31 img,.element__media,.vjsPlayingHistory,
.section__v2 .slot_4 .slot__content::after,#before-button, #after-button,
.section__v2 .slot_5 .slot__content::after,.comments__info,[class*=zpr_inside_]:after,
.hero-img-v34 .element:nth-child(4) .element__headline,.article__placement:after,
.hero-img-v34 .element:nth-child(5) .element__headline,
.element:nth-child(n+7) .element__content a::after
{background-color:var(--bg_1)!important;}
.article .related_articles__elements,
.two_columns_article__container .related_article__title
{background-color:var(--bg_3)!important;}
body,.header .bottom-tap-menu .bottom-tap-menu-right{background-color:var(--bg_3)!important;}
body,a,.listing.v18_side a,.rotator  p,footer .h5,.single-hit__title,.single-hit__author,
.section__v2  .slot__small.slot_4 .slot__title p,article .hightlight__text,
.section__v2  .slot__small.slot_5 .slot__title p,article p,.history-time-picker *,
.hero-img-v34 .element:nth-child(5) .element__headline,
.vjsPlayingHistory__hit__info *,.radioSchedule__title,
.v32.listing-container .element:nth-child(n+7) .element__content a
{color:var(--color_1)!important;}
h3,.h3
{color:var(--color_2)!important;}
footer li .h5
{color:#FF7900!important;}
#playerProgramName{color:var(--color_0)}

.artist-hits .single-hit:hover{background:#ffffff08;}

/* FIX */
body.noscroll{overflow-y:auto;}
div[class*="eska-pl_topboard"]{aspect-ratio:0!important;}
.section__rotator .section__slots .rotator::before
{background: linear-gradient(to left,#0000,var(--bg_1));}
.section__rotator .section__slots .rotator::after
{background: linear-gradient(to left,var(--bg_1),#0000);}
    /* fooper img */
.listing-container.v31 img{border-radius:25px;}

footer .gl_wrapper,footer .row{padding:0;}

.artist-hits .single-hit__vote,
.wide article .share-buttons > a
{filter:invert(1);}
.radioSchedule__program{box-shadow: 0px 8px 60px #ffffff40;}
/*anty ad-ad-block*/
adblock-detector-wrapper{display:none;}
}
@-moz-document domain(player.eska.pl){body{background:var(--bg_1)!important;}}
