@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.google.com") {

/** Fixed Navbar **/
.qp {
  background-color: rgba(44,44,44,0.8) !important;
  -moz-box-shadow: 0 1px 4px rgba(0,0,0,0.5);
  -webkit-box-shadow: 0 1px 4px rgba(0,0,0,0.5);
  box-shadow: 0 1px 4px rgba(0,0,0,0.5);
  border: none !important;
  width: 100%;
  position: fixed;
  z-index: 10000;
}
.aC .no:first-child {
  padding-top: 17px !important;
}

div#gbar, div#guser {
  height: 18px !important;
  padding: 5px 8px 2px 8px !important;
}
div#guser { padding-bottom: 2px !important }
div#gbar a, div#guser a {
  color: #F29000 !important;
  text-decoration: none !important;
}
.gb1, .a8 {
  color: #CCCCCC !important;
}


/**** Google Appbar ****/

/** Common settings **/
a.gb1.qq:before, a.gb3.qq:before, a.gb2.qq:before,
div#gbar > nobr > b.gb1:before {
  display: inline-block !important;
  margin: 0 3px 2px 0;
  vertical-align: middle;
}

/** Remove underline for Appbar **/
a.gb3.qq > u { text-decoration: none !important; }


/** gmail **/
div#gbar > nobr > b.gb1:before {
  content: url("data:image/png;base64,");
}

/** calendar **/
a.qq[href*="calendar"]:before {
  content: url("data:image/png;base64,");
}

/** docs **/
a.qq[href*="docs.google"]:before {
  content: url("data:image/png;base64,");
}

/** reader **/
a.qq[href*="reader"]:before {
  content: url("data:image/png;base64,");
}

/** orkut **/
a.qq[href*="orkut"]:before {
  content: url(data:image/ico;base64,) !important;
}

/** web **/
a.qq[href*="webhp?tab=mw"]:before {
  content: url("data:image/png;base64,");
}

/** more **/
a.qq[href$="/options/"]:before {
  content: url("data:image/png;base64,");
}
a.qq[href$="/options/"] > small { display:none }
a.qq[href$="/options/"] > u {
  background: url(data:image/png;base64,) no-repeat 32px 3px !important;
  padding-right: 17px !important;
}

/** picasa **/
a.qq[href*="picasaweb.google"]:before {
  content: url("data:image/png;base64,");

}

/** sites **/
a.qq[href*="sites.google"]:before {
  content: url("data:image/png;base64,");
}

/** groups **/
a.qq[href*="groups.google"]:before {
  content: url("data:image/png;base64,");
}

/** youtube **/
a.qq[href*="youtube.com"]:before {
  content: url("data:image/png;base64,");
}

/** images **/
a.qq[href*="images.google"]:before {
  content: url("data:image/png;base64,");
}

/** videos **/
a.qq[href*="video.google"]:before {
/*  content: url("data:image/png;base64,");*/
  content: url("data:image/png;base64,");
}

/** maps **/
a.qq[href*="maps.google"]:before {
  content: url("data:image/png;base64,");
}

/** news **/
a.qq[href*="news.google"]:before {
  content: url("data:image/png;base64,");
/*  content: url("data:image/png;base64,");*/
}

/** shopping **/
a.qq[href*="prdhp"]:before {
  content: url("data:image/png;base64,");
}

/** books **/
a.qq[href*="books.google"]:before {
  content: url("data:image/png;base64,");
}

/** translate **/
a.qq[href*="translate.google"]:before {
  content: url("data:image/png;base64,");
}

/** scholar **/
a.qq[href*="scholar.google"]:before {
  content: url("data:image/png;base64,");
}

/** finance **/
a.qq[href*="finance"]:before {
  content: url("data:image/png;base64,");
}

/** blogger **/
a.qq[href*="blogsearch.google"]:before {
  content: url("data:image/png;base64,");
}


/**** Userbar ****/

/** Common Settings **/
div#guser a.e:before {
  display: inline-block !important;
  margin: 0 3px 2px 0;
  vertical-align: middle;
}

/** Settings **/
a.e[href$="#settings"]:before {
  content: url("data:image/png;base64,");
}

/** help **/
a.e[href*="support/?ctx=gmail"]:before {
  content: url("data:image/png;base64,");
}

/** sign out **/
a.e[href*="?logout"]:before {
  content: url("data:image/png;base64,");
}

}
