@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.google.com") {

/** FONT SETTINGS **/
* { font-family: "Segoe UI",Consolas !important; font-size: 8pt !important; }

/** Hide Invite box **/
.nH.pY { display: none !important; }

/** Hide the ugly focus ring in FF **/
:focus { outline: 0 !important }

/** Gmail Logo **/
.a9 {
/*shiny*/  background: url(data:image/png;base64,) no-repeat -5px -3px !important;
  margin-top: 8px !important;
}

/** Search box **/
table.cf.d { margin:16px 0 0 16px !important }
.bQ {
  -moz-border-radius: 3px 0 0 3px;
  -webkit-border-radius: 3px 0 0 3px;
  border-radius: 3px 0 0 3px;
  -moz-box-shadow: 0 1px 1px rgba(0,0,0,0.3);
  -webkit-box-shadow: 0 1px 1px rgba(0,0,0,0.3);
  box-shadow: 0 1px 1px rgba(0,0,0,0.3);
  height: 18px;
  width: 300px;
  padding: 3px !important;
  margin:0 !important;
  border:none !important;
  border-right: 1px solid #DDD !important;
}
.bN.bM { padding:0 8px 0 0 !important; }
.bN.bM .L3 {
  padding: 3px 7px 3px !important;
  line-height:18px !important;
  -moz-border-radius:0 !important;
  -webkit-border-radius:0 !important;
  border-radius:0 !important;
  -moz-box-shadow: 0 1px 1px rgba(0,0,0,0.4);
  -webkit-box-shadow: 0 1px 1px rgba(0,0,0,0.4);
  box-shadow: 0 1px 1px rgba(0,0,0,0.4);
  margin-right:0 !important;
  border:none !important;
}
.bN.bM .L3:last-child {
  border-left: 1px solid #CCC !important;
  -moz-border-radius: 0 3px 3px 0 !important;
  -webkit-border-radius: 0 3px 3px 0 !important;
  border-radius: 0 3px 3px 0 !important;
  -moz-box-shadow: 1px 1px 1px rgba(0,0,0,0.4) !important;
  -webkit-box-shadow: 1px 1px 1px rgba(0,0,0,0.4) !important;
  box-shadow: 1px 1px 1px rgba(0,0,0,0.4) !important;
}
.bN.bR {
  background: transparent url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAoCAAAAAAa3c+rAAAAAXNSR0IArs4c6QAAABJJREFUCNdj+MnEQBT8CsUEIACgKQMyxVIz6AAAAABJRU5ErkJggg==) repeat-x left center !important;
  -moz-border-radius: 3px !important;
  -webkit-border-radius: 3px !important;
  border-radius: 3px !important;
  -moz-box-shadow: 1px 1px 1px rgba(0,0,0,0.4);
  -webkit-box-shadow: 1px 1px 1px rgba(0,0,0,0.4);
  box-shadow: 1px 1px 1px rgba(0,0,0,0.4);
  text-decoration: none !important;
  padding-left: 5px !important;
}
.bN.bR br { display:none }
.bN.bR span:first-child {
  background: url(data:image/png;base64,) no-repeat center left;
  padding: 2px 10px 2px 18px;
}
.bN.bR span:last-child {
  background: url(data:image/png;base64,) no-repeat center left;
  padding: 2px 10px 2px 20px;
}

/* Notifications */
.b8 { padding: 8px 0 !important; }

/** Settings pane **/
/* Rounded border for selected tab */
.fZ {
  -moz-border-radius-topleft: 3px !important;
  -moz-border-radius-topright: 3px !important;
  -webkit-border-top-left-radius: 3px !important;
  -webkit-border-top-right-radius: 3px !important;
  border-radius-topleft: 3px !important;
  border-radius-topright: 3px !important;
}
/* themes - rounded frame for current theme & hover */
.sh, .sk {
  -moz-border-radius: 3px !important;
  -webkit-border-radius: 3px !important;
  border-radius: 3px !important;
}

/** Buttons **/
.J-Zh-I { background: transparent url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAoCAAAAAAa3c+rAAAAAXNSR0IArs4c6QAAABJJREFUCNdj+MnEQBT8CsUEIACgKQMyxVIz6AAAAABJRU5ErkJggg==) repeat-x left center !important; }

/** Bottom bar margin **/
.l2 { padding-bottom: 10px !important; }
.l2 * { color: #1D4088 !important; background:none !important }

}
