/*-------------left menu-------------*/

a.icon.icon-home {
    color: #fff;
}
.wy-nav-side {
    position: fixed;
    top: 0;
    bottom: 0;
    left: 0;
    padding-bottom: 2em;
    width: 300px;
    overflow-x: hidden;
    overflow-y: hidden;
    min-height: 100%;
    color: #9b9b9b;
    background: #202327;
    z-index: 200;
    box-shadow: rgb(27 30 34) 0px 0px 17px 7px;
}
.wy-nav-side {
    position: fixed;
    top: 0;
