    overflow-y: hidden;
    min-height: 100%;
    color: #9b9b9b;
    background: #202327;
    z-index: 200;
}

.wy-menu-vertical li.toctree-l1.current>a {
    border-bottom: 1px solid #2d3034;
    border-top: 1px solid #2d3034;
}
.wy-menu-vertical li.current>a, .wy-menu-vertical li.on a {
    color: #f5792a;
    font-weight: 700;
    position: relative;
    background: #292e34;
    border: none;
    padding: 0.4045em 1.618em;
}
