    box-shadow: rgb(43 47 50) -4px -4px 10px 0px, rgb(19 21 24) 4px 4px 10px 0px;
    border-radius: 40px;
}
ul {
    margin: 10px 0 0;
    padding: 0;
    list-style: none;
    list-style-image: none;
}

.wy-menu-vertical li.current>a button.toctree-expand, .wy-menu-vertical li.on a button.toctree-expand {
