css:
@-moz-document url-prefix("https://platform.openai.com/playground") {
  #root, .app-header {
      background: black;
  }

  h1, h2, h3, h4, h5, h6 {
      color: #bbb !important;
  }

  .pg-alert.note {
    color: white;
  }

  .chat-gpt-btn {
    background: #111;
    border: 1px solid silver;
    color: white;
  }

  .btn-secondary.btn-minimal {
    color: white;
  }

  .subheading {
    color: var(--gray-400);
  }

  .chat-pg-message:hover {
    background-color: var(--gray-200);
  }

  .chat-pg-message:hover .chat-message-role .subheading .chat-message-role-text {
    background: var(--gray-800);
    color: black;
  }


  .chat-pg-message:hover .chat-message-role .subheading .chat-message-role-text:hover {
    background: var(--gray-300);
    color: white;
  }

  .chat-pg-message.active .text-input-with-focus .text-input {
    background-color: #222;
  }

  .css-i4h7a8-control, .css-81r2gk-control, .css-2krsg1-control, .text-input {
    background: #222;
  }

  .DraftEditor-root, .public-DraftEditorPlaceholder-root {
    /*color: white;*/
    background: #222;
  }

  .public-DraftEditorPlaceholder-inner {
    color: white !important;
  }

  .btn-primary.btn-filled {
    background-color: #aaa;
    color: black;
  }


  .btn-primary.btn-filled:hover {
    background-color: #eee;
    color: black;
  }

  svg {
    color: white;
  }

  svg path {
    stroke: white;
    fill: #007700;
  }

  .css-1sbw8c7-option {
    background: #333;
  }

  .mode-select-beta-tag {
    color: white;
  }

  textarea {
      background: black;
      color: white;
  }

  .css-1uv6pfg-menu, .css-26l3qy-menu {
    background: #222;
  }

  .css-1u8ecqi-control {
   background: #222; 
  }


  .css-upaktt-control, .css-upaktt-control:hover {
   background: #111; 
    border: 1px solid white;
  }

  :root {
      --primary: black;
      --primary-50: #050505;
      --primary-100: #111111;
      --primary-200: #222222;
      --primary-300: #333333;
      --primary-400: #444444;
      --primary-500: #555555;
      --primary-600: #666666;
      --primary-700: #777777;
      --primary-800: #888888;
      --primary-900: #999999;
      --primary-50a: #05050599;
      --primary-100a: #11111199;
      --secondary: white;
      --secondary-50: #f5f5f5;
      --secondary-100: #eeeeee;
      --secondary-200: #dddddd;
      --secondary-300: #cccccc;
      --secondary-400: #bbbbbb;
      --secondary-500: #aaaaaa;
      --secondary-600: #999999;
      --secondary-700: #888888;
      --secondary-800: #777777;
      --secondary-900: #666666;
      --secondary-100a: #eeeeee99;
      --green-50: #999999;
      --green-100: #999999;
      --green-200: #999999;
      --green-300: #999999;
      --green-400: #999999;
      --green-500: #999999;
      --green-600: #999999;
      --green-700: #999999;
      --green-800: #999999;
      --green-900: #999999;
      --green-100a: #999999;
      --red-50: #bbbbbb;
      --red-100: #bbbbbb;
      --red-200: #bbbbbb;
      --red-300: #bbbbbb;
      --red-400: #bbbbbb;
      --red-500: #bbbbbb;
      --red-600: #bbbbbb;
      --red-700: #bbbbbb;
      --red-800: #bbbbbb;
      --red-900: #bbbbbb;
      --red-100a: #bbbbbb;
      --gray-50: #333;
      --gray-100: #444;
      --gray-200: #555;
      --gray-300: #666;
      --gray-400: #777;
      --gray-500: #888;
      --gray-600: #999;
      --gray-700: #aaa;
      --gray-800: #bbb;
      --gray-900: #333;
      --medium-wash: black;
      --bg-color: black;
      --text-primary: black;
      --text-default: black;
      --text-secondary: black;
      --text-disabled: black;
      --text-error: black;
      --font-size-small: 16px;
      --input-border: var(--gray-300);
      --input-border-focus: var(--primary-600);
      --input-focus-ring: 0px 0px 0px 1px #10a37f;
      --icon-warning-color: black;
      --heading-margin-top: 30px;
      --heading-margin-bottom: 16px;
      --content-width: 760px;
      --content-v-padding: 40px;
      --content-h-padding: 56px;
      --border-radius: 4px;
      --sans-serif: "ColfaxAI",helvetica,sans-serif;
      --monospace: "Roboto Mono",sfmono-regular,consolas,liberation mono,menlo,courier,monospace;
  }

  .btn.btn-disabled-style {
      background: #555555;
      cursor: not-allowed;
      pointer-events: auto;
  }


  .btn.btn-disabled-style:hover {
      background: #656565;
  }
}
